import React from 'react';

const Produit = (props) => {
    const acheter = (e) => {
        let btn = e.target
        btn.innerHTML=""
        btn.classList.add("btnLoading")
        var l = document.createElement('div') 
        l.setAttribute("class","loader1")
        var b = document.createElement('div') 
        b.setAttribute("class","box")
        b.appendChild(l)
        console.log(b)
        btn.appendChild(b);
        setTimeout(() => {
            btn.innerHTML=""
            btn.classList.add("btnOK")
        },4000)

    }
    return (
        <div className="produit position-relative align-items-center">
            <span className="produitInfo position-absolute text-center">Nouveau</span>
            <div className="d-flex flex-column w-100">
                <img src={props.image} alt="" className="accueilProduitImage"/>
                <span className="accueilProduitNom">
                    {props.nom}
                </span>
                <p className="accueilProduitSousNom">
                    {props.sousNom}
                </p>
            </div>
            <div className="d-flex flex-column align-items-center">
                <div className="d-flex">
                    {
                        props.prixSansReduc 
                        ?
                        <del className="accueilProduitPrixBarre me-1">
                            {props.prix} €
                        </del>
                        :
                        ""                         
                    }
                    <span className="accueilProduitPrix ms-1">
                        {props.prix} €
                    </span>                    
                </div>

                <button className="accueilProduitBtn" onClick={(e) => acheter(e)}>
                    Acheter
                </button>
            </div>
        </div>
    );
}

export default Produit;
