    const initDemande = () => {
        console.log("p=",props)
        const di = props.initdemande(props.setdemande)
        console.log("di=",props.demande)
    }
    const beFriend = async (email) =>{
        const db = getFirestore();
        const e = async (email) => {
            console.log("ok")
            await setDoc(doc(db, "friends", uuid() ), {
                email1: auth.currentUser.email,
                email2: email,
              });
            await deleteDoc(doc(db, "request", where("email1", "==", email) , where("email2", "==", auth.currentUser.email)));
            }
          e(email)
    }
    var amis = props.demande.map((a) =>
    <div class="d-flex justify-content-between align-items-center bg-white p-2 rounded rounded-5 mb-2">
        <div class="d-flex align-items-center">
            <span class="rounded rounded-5 me-2 bg-dark storyProfile"></span>
            <strong class="text-capitalize">{a.firstName} {a.lastName}</strong>
        </div>
        <i class="fas fa-ellipsis-h text-secondary fa-2x p-1"></i>
    </div>
    )
    var nb = props.ami.length
    return (
        <div class="bg-light rounded rounded-5 p-2 mb-2">
            <div class="d-flex justify-content-between align-items-center">
                <span class="text-secondary text-uppercase p-2">amis</span>
                <span class="bg-grey p-2 m-2  text-light pill rounded-pill badge text-center d-flex align-items-center justify-content-center">nb</span>
            </div>
                        
	
        </div>
    );
}

export default Amis;
