import React from 'react';

function Demande(props) {
    var demandes = props.demande
    const initDemande = () => {
        console.log("p=",props)
        const di = props.initdemande(props.setdemande)
        console.log("di=",props.demande)
        props.demande.map((d) =>
        <div key={d.email1} class="rounded rounded-5 p-2 bg-white d-flex flex-column mb-2">
            <div class="d-flex align-items-center">
                <span class="rounded rounded-5 me-2 bg-dark storyProfile "></span>
                <span class="text-secondary">
                    <strong class="text-capitalize">{d.email1}</strong> veut t'ajouter a ses amis
                </span>
            </div>
            <div class="d-flex py-2">
                <button class="btn bg-dark rounded rounded-5 text-uppercase text-light me-3">accepter</button>
                <button class="btn border border-2 border-dark rounded rounded-5 text-uppercase text-dark me-3">refuser</button>
            </div>
        </div>
        
        )
    }
    return (            			
			<div class="bg-light rounded rounded-5 p-2 mb-2 w-100">
				<div class="d-flex justify-content-between align-items-center">
					<span class="text-secondary text-uppercase p-2" onLoad={initDemande()}>demandes</span>
					<span class="bg-danger p-2 m-2 rounded-circle text-light notifNumber text-center d-flex align-items-center justify-content-center">2</span>
				</div>
				<div class="w-100 d-flex flex-column gap-y-2">
                    {demandes}
				</div>
			</div>
    );
}

export default Demande;
