import React from "react";
import { auth } from "../../firebase_config";
import logo from "../assets/img/rds.png"

function Menu(props){
    const navItems = [
        {
            nom:"Accueil",
            action:"",
            active:true,
            notification:0
        },
        {
            nom:"Les défis",
            action:"",
            active:"",
            notification:0
        },
        {
            nom:"Mon showroom",
            action:"",
            active:"",
            notification:0
        },
        {
            nom:"Mes notes",
            action:"",
            active:"",
            notification:0
        },
        {
            nom:"Conseils",
            action:"",
            active:"",
            notification:0
        }
    ]

    const items = navItems.map(it => 
        <li className="list-style-none p-2 ps-0 d-flex justify-content-between" onClick={it.action}>
            <div className="ms-4 menuIt w-100 py-2 d-flex justify-content-between">
                <div className="d-flex align-items-center menuName">
                    <i className="fas fa-suitcase-rolling fa-2x text-secondary me-3"></i>
                    <span className={`text-uppercase ms-2 " ${it.active ? "text-danger" : "text-secondary"}`}>
                    {it.nom}
                    </span>
                </div>
                <span className={`bg-danger p-2 m-2  text-light pill rounded-pill badge text-center d-flex align-items-center justify-content-center ${ it.notification==0 ? "d-none" : ""}`}>
                    {it.notification}
                </span>
            </div>
        </li>
    )
  return (
    <div className="w-100 d-flex flex-column align-items-center h-100 position-relative">
        <img src={logo} alt="" className="logo" />
        <div className="w-100 d-flex align-items-center h-100">
          <ul className="p-1 py-3 bg-dark leftMenu">
            { items }
          </ul>
      </div>
    </div>
  );
};

export default Menu;
