import React from 'react';

const Post = (props) => {
	const post = [
		{
			user:{
				username:"NanouBinkz",
				pp:""
			},
			media:[
				{
					titre:"",
					url:""
				},
				{
					titre:"",
					url:""
				},
				{
					titre:"",
					url:""
				},
				{
					titre:"",
					url:""
				},
			],
			titre:"Un titre de post assez interressant, limite vulgaire pour faire plus de clique",
			date:"Il y a 4 heures",
			mediaNb:4,
			commentaire:[
				{

				}
			],
			commentaireNb:33,
			likeNb:134,

		}
	]

	const posts = post.map(p => 
				<div className="d-flex flex-column align-items-center w-100 mb-3 bg-white rounded rounded-5 p-3">
					<div className="d-flex justify-content-between align-items-center w-100 mb-2">
						<div className="d-flex align-items-center">
							<span className="rounded rounded-5 me-2 bg-dark storyProfile"style={{backgroundImage:`url(${p.user.pp})`}}></span>
							<div className="d-flex flex-column ">	
								<strong className="text-secondary text-capitalize ps-2">{p.user.username}</strong>
								<span className="text-secondary ps-2">{p.date}</span>
							</div>
						</div>
						<i className="fas fa-angle-right px-2 fa-2x"></i>
					</div>
					<p className="p-1 text-dark m-0 pb-0 w-100 text-start">{p.titre}</p>
					<div className="container-fluid p-1 postContent">
						<div className="row">
							<div className="col-6 p-2 pe-2 pb-1" >
								<div className="rounded rounded-5 bg-dark w-100" >
									
								</div>
							</div>
							<div className="col-6 p-2 d-flex flex-column gap-3">
									<div className="w-100 bg-dark rounded rounded-5" >
										
									</div>
									<div className="w-100 bg-dark rounded rounded-5" >
										
									</div>
							</div>
						</div>
					</div>
					
					<div className="d-flex justify-content-between align-items-center w-100 mb-2 p-1">
						<div className="d-flex align-items-center">
							<span className="d-flex align-items-center">
								<i className="far fa-heart fa-2x me-2"></i>
								{p.commentaireNb}
							</span>
							<span className="ms-4 d-flex align-items-center">
								<i className="far fa-comment fa-2x me-2"></i>
								{p.likeNb}
							</span>
						</div>
					<i className="fas fa-share px-2 fa-2x"></i>
					</div>
					
				</div>
		)
    return (
        <div className="">		
				{posts}
			</div>
    );
}

export default Post;
