import React from 'react';

const Produit = (props) => {
    const acheter = (e) => {
        let btn = e.target
        btn.innerHTML=""
        btn.classList.add("btnLoading")
        var l = (<div class='box'><div class='loader1'></div></div>);
        console.log(l)
        btn.innerHTML=l
        btn.appendChild(l);

    }
    return (
        <div className="produit">
            <div className="d-flex flex-column w-100">
                <img src={props.image} alt="" className="accueilProduitImage"/>
                <span className="accueilProduitNom">
                    {props.nom}
                </span>
                <p className="accueilProduitSousNom">
                    {props.sousNom}
                </p>
            </div>
            <div className="d-flex flex-column align-items-center">
                <span className="accueilProduitPrix">
                    {props.prix} â‚¬
                </span>
                <button className="accueilProduitBtn" onClick={(e) => acheter(e)}>
                    Acheter
                </button>
            </div>
        </div>
    );
}

export default Produit;
