import React from 'react';
import Search from "../assets/icon/search.png"

function Defi(props){
	const defis_json = [
		{
			titre:"Chic en blanc pour aller fluncher",
			semaine:"24 au 30 janvier 2022",
			date_debut:"24/01/2022",
			date_fin:"30/01/2022",
			regle:[
				{
					nom:"Budjet",
					valeur:"200 €"
				},
				{
					nom:"Type de commerce",
					valeur:"Boutique physique et en ligne"
				},
				{
					nom:"Delai",
					valeur:"Soumettre sa tenu avant la date de fin"
				},
			],
			img:'https://media3.giphy.com/media/9r4YOFonVVftHd3Q5A/giphy.gif',
			participantNb:3,
			fini:false,
		},
		{
			titre:"Moderne en Desigual",
			semaine:"17 au 23 janvier 2022",
			date_debut:"17/01/2022",
			date_fin:"23/01/2022",
			regle:[
				{
					nom:"Budjet",
					valeur:"250 €"
				},
				{
					nom:"Type de commerce",
					valeur:"Boutique physique uniquement"
				},
				{
					nom:"Contrainte d'achat",
					valeur:"au moin une pièce de la marque Desigual"
				},
				{
					nom:"Delai",
					valeur:"Soumettre sa tenu avant la date de fin"
				},
			],
			img:"https://i.pinimg.com/originals/8b/fc/65/8bfc6582ab718cd5ff40100090694121.gif",
			participantNb:3,
			fini:true,
		},
		{
			titre:"Sexy pour voler la vedette au mariage de ta meilleure amie",
			semaine:"10 au 16 janvier 2022",
			date_debut:"10/01/2022",
			date_fin:"16/01/2022",
			regle:[
				{
					nom:"Budget",
					valeur:"350 €"
				},
				{
					nom:"Type de commerce",
					valeur:"Boutique physique et en ligne"
				},
				{
					nom:"Delai",
					valeur:"Soumettre sa tenu avant la date de fin"
				},
			],
			img:"https://media.giphy.com/media/1APg5zWLqaBPjpIOYf/giphy.gif",
			participantNb:3,
			fini:true,
		},
		{
			titre:"Glamour pour manger une croziflette",
			semaine:"3 au 9 janvier 2022",
			date_debut:"03/01/2022",
			date_fin:"09/01/2022",
			regle:[
				{
					nom:"Budjet",
					valeur:"200 €"
				},
				{
					nom:"Type de commerce",
					valeur:"Boutique physique et en ligne"
				},
				{
					nom:"Delai",
					valeur:"Soumettre sa tenu avant la date de fin"
				},
			],
			img:"https://c.tenor.com/4Wb2KheHzKcAAAAC/tina-cipollari-eat.gif",
			participantNb:120,
			fini:true,
		},
	]

	const defi = defis_json.map((d) =>
		<div className='w-100 mb-0'>
			{
				(d.fini===false) ?
				<div className="defi">
					<div className="d-flex">
						<div className="d-flex flex-column w-100">
							<div className="d-flex flex-column w-100">
								<h4 className="defiDate">
									Défi de la semaine du {d.semaine}	
								</h4>				
								<h3 className="defiName">
									{d.titre}
								</h3>
								<div className="d-flex align-items-center">
									<ul className="defiRegleList">
										<span className='mb-2'>Les règles :</span>
										{d.regle.map(r => 
										<li className="defiRegle">
											<span>{r.nom} :</span>
											<span className='fw-bold'> {r.valeur}</span>
										</li>	
										)}
									</ul>
								</div>
							</div>
							<div className="d-flex align-items-center mt-2">
								<button className="defiBtn bg-secondary">Participer au defi</button>
								<button className="defiBtn bg-secondary">Voir le Showroom</button>
								<p className="defiParticipant">
									déja {d.participantNb} participants pour ce défi
								</p>
							</div>
						</div>
						<img src={d.img} alt="" className="defiImg" />
					</div>
				</div>
				:
				<div className="defi fini">
					<div className="d-flex">
						<div className="d-flex flex-column w-100">
							<h4 className="defiDate text-secondary">
								Défi de la semaine du {d.semaine}	
							</h4>				
							<h3 className="defiName text-dark">
								{d.titre}
							</h3>
							<div className="d-flex align-items-center">
								<button className="defiBtn bg-secondary">Voir le Showroom</button>
								<p className="defiParticipant text-secondary">
									{d.participantNb} ont participé à ce défi
								</p>
							</div>
						</div>
						<img src={d.img} alt="" className="defiImg" />
					</div>

				</div>
			}
		</div>
	)

    return (
		<div className=" d-flex flex-column justify-content-center align-items-center ">
			<form action="" className="w-100 d-flex px-5">
				<div className="search">
					<input type="text" name="" id="" className="w-100 p-2 searchInput" placeholder="Rechercher un défi"/>
					<button className="searchBtn" type="submit">
						<img src={Search} alt="" className="icon-s" />
					</button>
				</div>
			</form>
			<div className="d-flex my-3 px-5">
				<button className="defiBtn bg-black triActive">Les Nouveau</button>
				<button className="defiBtn bg-black">Les Populaire</button>
				<button className="defiBtn bg-black">Les Anciens</button>
				<button className="defiBtn bg-black">Les Drôles</button>
			</div>
			{defi}
		</div>  
    );
}
export default Defi;
