import React, { useEffect, useState } from "react";
import ApiService from "../components/api";
import {
  PaymentElement,
  useStripe,
  useElements
} from "@stripe/react-stripe-js";

export default function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();

  const [message, setMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (!stripe) {
      return;
    }

    const clientSecret = new URLSearchParams(window.location.search).get(
      "payment_intent_client_secret"
    );

    if (!clientSecret) {
      return;
    }

    stripe.retrievePaymentIntent(clientSecret).then(({ paymentIntent }) => {
      switch (paymentIntent.status) {
        case "succeeded":
          setMessage("Payment succeeded!");
          break;
        case "processing":
          setMessage("Your payment is processing.");
          break;
        case "requires_payment_method":
          setMessage("Your payment was not successful, please try again.");
          break;
        default:
          setMessage("Something went wrong.");
          break;
      }
    });
  }, [stripe]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const card = elements.getElement(PaymentElement);
   
   // add these lines
    const {paymentMethod, error} = await stripe.createPaymentMethod({
       type: 'card',
       card: card
    });
    ApiService.saveStripeInfo({
        email, payment_method_id: paymentMethod.id})
    .then(response => {
        console.log(response.data);
    }).catch(error => {
        console.log(error)
    })
  }

  return (
    <form className="f" id="payment-form" onSubmit={handleSubmit}>
      <PaymentElement id="payment-element" />
      <button className="btt" disabled={isLoading || !stripe || !elements} id="submit">
        <span id="button-text">
          {isLoading ? <div className="spinner" id="spinner"></div> : "Pay now"}
        </span>
      </button>
      {/* Show any error or success messages */}
      {message && <div id="payment-message">{message}</div>}
    </form>
  );
}
