import React, { Component } from "react";
import googleLogo from "../assets/icon/google-logo.png"
import { Link, Navigate } from "react-router-dom";
import { signInWithPopup, GoogleAuthProvider } from "firebase/auth";
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";
import { auth } from '../../firebase_config'
import $ from 'jquery'
import { async } from '@firebase/util';

class Login extends Component {
    constructor(props) {
      super(props);
      this.state = {
          validationEmail: "",
          validationPassword: "",
        };
      this.inputs = React.createRef();
      this.inputs.current = []

      this.addInputs = async (el) => {
        if (el && !this.inputs.current.includes(el)) {
        this.inputs.current.push(el);
        }
        //this.inputs.Current.push(el)
        console.log(this.inputs.current)
      };
      this.formRef = React.createRef();

      this.setValidationEmail = (msg) => {
          this.setState({
              validationEmail: msg
          })
      }

      this.setValidationPassword = (msg) => {
          this.setState({
              validationPassword: msg
          })
      }

      this.signIn = async (e) => {
        e.preventDefault();
        const signingIn = (email, pwd) => signInWithEmailAndPassword(auth, email, pwd)
      
          try {
      
            const cred = await signingIn(
              this.inputs.current[0].value,
              this.inputs.current[1].value
            )
      
          } catch (err) {
            console.log(err)
            if(err.code === "auth/invalid-email") {
              this.setValidationEmail("Email format invalid")
            }
      
            if(err.code === "auth/wrong-password") {
              this.setValidationPassword("Wrong password")
            }
            
          }
      }

      this.signWithGoogle = async (e) => {
          //e.preventDefault();
          const provider = new GoogleAuthProvider();
          provider.setCustomParameters({
            'allow_signup': 'false'
          });
          console.log(auth)
          await signInWithPopup(auth, provider)
            .then((result) => {
                console.log(result)
              // This gives you a Google Access Token. You can use it to access the Google API.
              const credential = GoogleAuthProvider.credentialFromResult(result);
              const token = credential.accessToken;
              // The signed-in user info.
              const user = result.user;
              // ...
            }).catch((error) => {
                console.log(error)
              // Handle Errors here.
              const errorCode = error.code;
              const errorMessage = error.message;
              // The email of the user's account used.
              const email = error.email;
              // The AuthCredential type that was used.
              const credential = GoogleAuthProvider.credentialFromError(error);
              // ...
            });
         
      };
    }
    render() {
        return (
            <div className="vh-100 vw-100 m-0 p-0 overflow-x-hidden">

        <div className="row h-100 rounded rounded-5 justify-content-end">
          <div
            id="three"
            className="col-md-6 vh-100 position-fixed start-0"
            style={{ backgroundImage: `url(${bg})`, backgroundSize: "cover" }}
          >
            <div
              id="carouselExampleControls"
              className="carousel slide p-5 w-100 h-100"
              data-bs-ride="carousel"
            >
              <div className="carousel-inner" role="listbox">
                <div className="carousel-item active">
                  <img src="" alt="First slide"></img>
                </div>
                <div className="carousel-item">
                  <img src="" alt="Second slide"></img>
                </div>
                <div className="carousel-item">
                  <img src="" alt="Third slide"></img>
                </div>
              </div>
              <div className="position-absolute start-50 bottom-0 translate-middle mb-5 d-flex">
                <button
                  className="rounded rounded-circle p-3 mx-2 border-0 bg-secondary text-light icon d-flex justify-content-center align-items-center carouselBtn"
                  type="button"
                  data-bs-target="#carouselExampleControls"
                  data-bs-slide="prev"
                >
                  <FontAwesomeIcon icon={faArrowLeft} />
                </button>
                <button
                  className="rounded rounded-circle p-3 mx-2 border-0 bg-secondary text-light icon d-flex justify-content-center align-items-center carouselBtn"
                  type="button"
                  data-bs-target="#carouselExampleControls"
                  data-bs-slide="next"
                >
                  <FontAwesomeIcon icon={faArrowRight} />
                </button>
              </div>
            </div>
          </div>
            <div
                className="col-md-6 d-flex flex-column justify-content-center align-items-center"
                style={{
                    paddingLeft: "200px",
                    paddingRight: "200px",
                }}
            >
                <Link className="position-absolute start-50 top-0 m-5" to="/">
                    Back To Home
                </Link>
                <h1 className="w-100 text-center fw-bold"> Sign in to Clever </h1>
                <div className="d-flex justify-content-center my-3">
                    <div
                        onClick={this.signWithGoogle}
                        className="rounded rounded-5 bg-light p-4 m-2 icon-container"
                    >
                        <img src={googleLogo} className="icon" alt="" />
                    </div> 
                </div>
                <div
                    className="w-100 d-flex justify-content-center align-items-center my-4 mb-0"
                    style={{
                        height: "2px",
                        background: "#ddd",
                    }}
                >
                    <small className="bg-white m-0 px-3 py-2">
                        
                        or do it via E-mail
                    </small>
                </div>
                <form className="m-0 p-0 w-100" onSubmit={this.signIn}>
                    <label
                        htmlFor="email"
                        className="text-capitalize fw-bold d-flex flex-column w-100 my-4"
                    >
                        Email
                        <input
                            type="text"
                            className="rounded rounded-4 mt-1 p-3 text-secondary bg-light border-0 text-secondary formInput"
                            placeholder="adresse@mail.com"
                            ref={this.addInputs}
                            required
                        />
                        <small className="text-danger fw-bold mt-1">
                            {this.state.validationEmail}
                        </small>
                    </label>
                    <label
                        htmlFor="email"
                        className="text-capitalize fw-bold d-flex flex-column w-100 my-4"
                    >
                        Password
                        <input
                            type="password"
                            className="rounded rounded-4 mt-1 p-3 text-secondary bg-light border-0 text-secondary formInput"
                            placeholder="password"
                            ref={this.addInputs}
                            required
                        />
                        <small className="text-danger fw-bold mt-1">
                            {this.state.validationPassword}
                        </small>
                    </label>
                    <div className="d-flex justify-content-between w-100 my-4">
                        <div class="form-check">
                            <input
                                type="checkbox"
                                className="form-check-input formInput"
                                name=""
                                id=""
                                value="checkedValue"
                            />
                            <label className="form-check-label" for="">
                                Remember me
                            </label>
                        </div>
                        <a href="" className="text-primary text-capitalize fw-bold">
                            
                            forgot password ?
                        </a>
                    </div>
                    <button className="btn btn-primary w-100 my-4 rounded rounded-5" type="submit">
                        
                        Sign in
                    </button>
                    <div className="w-100 d-flex justify-content-center">
                    <Link className="text-center" to="/signup">
                        Not registered ?
                    </Link>
                </div>
                </form>
                <small className="text-center text-secondary my-4 text-capitalize">
                    @2021 all right reserved
                </small>
            </div>
            </div>
            </div>
        );
    }
}

export default Login;
