import React, {useEffect, useState}  from 'react';

const Avis = (props) => {
    return (
        <div className="avis m-2">
            <div className="avisNote">
                <div className="d-flex align-items-end">
                    <span className="note">
                        {props.note}
                    </span>
                    <ReactStars
                        count={5}
                        onChange={(e) => {
                            $(`#note-${a.id}`).val(e)
                        }}
                        edit={false}
                        size={48}
                        activeColor="#ffd700"
                    />    
                </div>
                <span className="avisEtoiles text-align-center">
                    ****
                </span>
            </div>
            <img src={props.produit.image1} alt="" className="imgM" />
            <div className="d-flex flex-column jusitify-content-between ms-3">
                <div className="d-flex flex-column mb-2">
                    <span className="fw-bold">
                        {props.titre}
                    </span>
                    <span className="avisDate">
                        L{props.date} post√© par {props.user}
                    </span>
                </div>
                <div className="d-flex flex-column">
                    <p className="avisText">
                        {props.contenu}
                    </p>
                </div>
            </div>
        </div>
    );
}

export default Avis;
