import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRight, faArrowLeft } from '@fortawesome/free-solid-svg-icons'
import bg from "./assets/img/rules-bg.jpg"
import Login from './components/login';
import SignUp from './components/signup';
import { BrowserRouter, Routes, Route, } from "react-router-dom";
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { RGBELoader } from 'three/examples/jsm/loaders/RGBELoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';

class Connexion extends Component {
    render() {
        return (
            <div className='vh-100 vw-100 m-0 p-0 overflow-x-hidden'>
                <div className="row h-100 rounded rounded-5 justify-content-end">
                    <div 
                    id="three"
                    className="col-md-6 vh-100 position-fixed start-0" 
                    style={{ backgroundImage:`url(${bg})`,backgroundSize:"cover" }}>
                        <div id="carouselExampleControls" className="carousel slide p-5 w-100 h-100" data-bs-ride="carousel">
                            <div class="carousel-inner" role="listbox">
                                <div className="carousel-item active">
                                    <img src="" alt="First slide"></img>
                                </div>
                                <div className="carousel-item">
                                    <img src="" alt="Second slide"></img>
                                </div>
                                <div className="carousel-item">
                                    <img src="" alt="Third slide"></img>
                                </div>
                            </div>
                            <div className="position-absolute start-50 bottom-0 translate-middle mb-5 d-flex">
                                <button className="rounded rounded-circle p-3 mx-2 border-0 bg-secondary text-light icon d-flex justify-content-center align-items-center carouselBtn" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
                                    <FontAwesomeIcon icon={faArrowLeft} />
                                </button>
                                <button className="rounded rounded-circle p-3 mx-2 border-0 bg-secondary text-light icon d-flex justify-content-center align-items-center carouselBtn" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
                                    <FontAwesomeIcon icon={faArrowRight} />
                                </button>
                            </div>
                        </div>
                    </div>
                    <Routes>
                        <Route path="/signup" element={ <SignUp/> } />
                        <Route path="/signin" element={ <Login/> } />
                    </Routes>
                </div>
            </div>
        );
    }
}

let camera, scene, renderer;

init();
render();

function init() {

  const container = document.createElement( 'div' );
  document.getElementById("threejs").appendChild( container );

  camera = new THREE.PerspectiveCamera( 45, window.innerWidth / window.innerHeight, 0.25, 20 );
  camera.position.set( - 1.8, 0.6, 2.7 );

  scene = new THREE.Scene();

  new RGBELoader()
    .load( 'environment.hdr', function ( texture ) {

      texture.mapping = THREE.EquirectangularReflectionMapping;

      scene.background = texture;
      scene.environment = texture;

      render();

      // model

      const loader = new GLTFLoader();
      loader.load( 'suzanne.gltf', function ( gltf ) {

        scene.add( gltf.scene );

        render();

      } );

    } );

  renderer = new THREE.WebGLRenderer( { antialias: true } );
  renderer.setPixelRatio( window.devicePixelRatio );
  renderer.setSize( window.innerWidth/2, window.innerHeight );
  renderer.toneMapping = THREE.ACESFilmicToneMapping;
  renderer.toneMappingExposure = 1;
  renderer.outputEncoding = THREE.sRGBEncoding;
  container.appendChild( renderer.domElement );

  const controls = new OrbitControls( camera, renderer.domElement );
  controls.addEventListener( 'change', render ); // use if there is no animation loop
  controls.minDistance = 2;
  controls.maxDistance = 10;
  controls.target.set( 0, 0, - 0.2 );
  controls.update();

  window.addEventListener( 'resize', onWindowResize );

}

function onWindowResize() {

  camera.aspect = window.innerWidth / window.innerHeight;
  camera.updateProjectionMatrix();

  renderer.setSize( window.innerWidth, window.innerHeight );

  render();

}

//

function render() {

  renderer.render( scene, camera );

}


export default Connexion;
