	const setNote = (el) =>{
		const elc = document.getElementsByClassName("formNote")
		for (let index = 0; index < elc.length; index++) {
			elc[index].children[0].classList.remove("noteValide");
			
		}
		el.target.classList.add("noteValide")
		document.getElementById("noteValue").value = el.target.parentElement.children[1].innerHTML;

	}

	const changePhoto = (p) =>{
		const el = "p"+p.id
		const index = document.getElementById(el)
		let i = index.innerHTML
		let newi = 0
		console.log("p",p)
		if (Number(i)===p.complete.photo.length-1){
			newi = 0
		}
		else{
			newi = Number(i)+1
		}
		index.innerHTML = newi
		index.parentElement.children[1].src = p.complete.photo[newi]
	}

