					<>
					<motion.div
						style={{
							position: "fixed",
							top: "0%",
							bottom: "0%",
							left: "0%",
							right: "0%",
							zIndex:9999,
							backgroundColor: "rgba(255,255,255,0.5)",
							display: "flex",
							justifyContent: "center",
							alignItems: "center",
							cursor: "pointer"
						}}
						initial={{ opacity: 0, height: 0, width: 0 }}
						animate={{ opacity: 1, height: "100vh", width: "100vw" }}
						exit={{ opacity: 0, height: 0, width: 0 }}
						onTap={()=> setVisible(!isVisible)}
					></motion.div>
					<AnimatePresence>
						{isVisible && (
							<motion.div
								style={{
									width: 0,
									height: 0,
									borderRadius: 15,
									backgroundColor: "#fff",
									zIndex:9999,
