import React from 'react';
import Avis from './Avis';

const Avislist = (props) => {
    console.log(props.avis)
    const avis = props.avis
    const avisList = avis.map(a => <Avis note={a.note} titre={a.titre} date={a.date} user={a.user.username} contenu={a.contenu} />)
    return (
        <>

            <div className="produitBuyCommentaire">
                <span className="caracteristiquesTitre">
                    Vous venez de recevoir ..., donnez nous votre avis sur ce produit
                </span>
                <form action="" className="d-flex w-100 commentPost">
                    <div className="w-100 d-flex flex-column p-2">
                        <input type="number" name="" id="" min="0" max="5" />
                        <input type="text" className="commentInput " placeholder="Titre de votre avis" />
                        <textarea name="" id="" cols="30" rows="2" className="commentInput mb-2" placeholder="Donner votre avis sur ce produit" required></textarea>
                    </div>
                    <button className="commentBtn m-2">
                        Poser
                    </button>
                </form>
            </div>
            {avisList}
        </>
    );
}

export default Avislist;
