import React, {useEffect, useState}  from 'react';
import $ from 'jquery';
const Panier = (props) => {
    console.log('props panier',props)
    const [produits,setProduits] = useState('')
    const [panierItems, setPanieritems] = useState([''])
    const [panier,setPanier] = useState('')
    const [u,setU] = useState({id:0,user:{first_name:"",last_name:""}})
    console.log('u u 168161',u,panierItems)
    const getCSRF = async () => {
    var url2 = "http://localhost:8000/csrf/"
    let r = '' 
    await fetch(url2, {
        credentials: 'include',
      }).then(response => {
        if(response.ok) {
            console.log("fetchCSRF")
            r = response.json();
        } else {
            console.log(response,'Mauvaise réponse du réseau');
        }
        })
        .catch(function(error) {
        console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
        });
    return r
    }

    console.log('props ppp produit',props)
    useEffect(async () => {
        let t = async () => {
            var url = "http://localhost:8000/current_user/?format=json"
            let r = await getCSRF()
            const csrftoken = r.csrfToken
            let data = await fetch(url,{                
              headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
              'X-CSRFToken': csrftoken
            }, 
            credentials: 'include',
            }).then(response => {
            if(response.ok) {
                console.log("current_user ttt",response)
                return response.json();
            } else {
                console.log(response,'Mauvaise réponse du réseau');
            }
            }).then(data => 
                {
                  return(data)
                }
                )
            .catch(function(error) {
              console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
            });
            setU(data)
          }
          await t()
          console.log("pas de userrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrrr", u.id)

          if(u.id!=undefined || u!='pas trouvé' || u.id!=0){
          console.log("pas de 88888888888888888888888888888888888888888888888888888888")

            if(u.id!=0 || u.id!= undefined){
                var url2 = `http://localhost:8000/panier/${u.id}/`
        
                let data2 = await fetch(url2).then(response => {
                if(response.ok) {
                    console.log("fetch")
                    return response.json();
                } else {
                    console.log(response,'Mauvaise réponse du réseau');
                }
                }).then(data => 
                    {return data}
                    )
                .catch(function(error) {
                console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
                });
                setPanieritems(data2)
                setPanier(data2[0].panier)
                console.log("ddata1151",panierItems)
            }
        }
        else{
              
            console.log("pas de user")
            if($(".panierContainer").hasClass("d-none")){
                $(".panierContainer").removeClass("d-none")
                $("body").addClass('noScroll')            
                var url = `http://localhost:8000/panier/0/`
                let produits = localStorage.getItem('panier')
                var obj = JSON.parse(produits);
                console.log(obj)
                let inputsForm = new Object
                let t = []
                if(produits!=null || produits!=undefined){
                    for(var i in obj){
                        console.log(obj[i]);
                        t.push(obj[i].id)                    
                    }

                }
                console.log('ttttttttttttttttttttttttttttt',t)
                inputsForm['id']=t
                console.log(inputsForm)
                inputsForm = JSON.stringify(inputsForm)
                let data = await fetch(url,{
                    method: 'POST',
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json',
                      },
                      body: inputsForm
                  }).then(response => {
                if(response.ok) {
                    console.log("fetch")
                    return response.json();
                } else {
                    console.log(response,'Mauvaise réponse du réseau');
                }
                }).then(data => 
                    {return data}
                    )
                .catch(function(error) {
                console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
                });
                setPanieritems(data)
            }
            else{
                $(".panierContainer").addClass("d-none")
                $("body").removeClass('noScroll')
            }
    
            console.log('panier ppp',panier)

          }
        return () => {
        };
    }, [u.id]);


    let modif = true
    if(props.modif==false){
        modif=false
    }

    useEffect(() => {
        if(panierItems[0]!='')
        {
        let prods = panierItems.map(p => 
            <div className="panierProduit">
                <div className="panierProduitG">
                    <img src={p.produit.image1} alt="" className="panierProduitImage"/>
                        <span className="panierProduitNom">
                            {p.produit.nom}
                        </span>
                </div>
                <div className="panierProduitDetails w-100">
                    <div className="d-flex mb-2">
                        <span>Prix</span>
                        <span className="fw-bold ms-2">{p.produit.prix} €</span>
                    </div>
                    <div className="d-flex align-items-md-center flex-column flex-md-row mb-2">
                        <span className="mb-1 mb-md-0">Quantité</span>
                        {
                        modif ?
                        <>
                        <div className="d-flex align-items-center">
                            <input type="number" className="ProduitBuyQte" min="1" max={p.produit.stock} defaultValue={p.quantite}/>
                                <button className="produitBuyBtn">Modifier</button>
                        </div>
                        <span className="ms-2 fs-6 mt-1 mt-md-0" onClick={(e,id) => deleteProduit(e,p.produit.id)} >supprimer</span>
                        </>
                        :
                        <span className="ProduitBuyQte">
                            {p.quantite}
                        </span>
                        }
                    </div>
                    <div className="d-flex">
                        <span>Sous-Total</span>
                        <span className="fw-bold ms-2">{p.quantite * p.produit.prix} €</span>
                    </div>
                </div>
            </div>
            )
            setProduits(prods)
                    }
    }, [panierItems]);
    
    const deleteProduit = async (e,id) =>{
        e.preventDefault()
        let inputsForm = new Object
        inputsForm['iduser'] = String(u.id)
        inputsForm['idproduit'] = String(id)
        inputsForm = JSON.stringify(inputsForm);
        var url2 = "http://localhost:8000/supprimer_produit/"

        await fetch(url2,{
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
              }, 
              body: inputsForm
          }).then(response => {
        if(response.ok) {
            console.log("fetchAddProduit")
            return response.json();
        } else {
            console.log(response,'Mauvaise réponse du réseau pas d ajout');
        }
        }).then(data => 
            {
                console.log(data)
            }
            )
        .catch(function(error) {
        console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
        });
        var url2 = `http://localhost:8000/panier/${u.id}/`

        let data2 = await fetch(url2).then(response => {
        if(response.ok) {
            console.log("fetch")
            return response.json();
        } else {
            console.log(response,'Mauvaise réponse du réseau');
        }
        }).then(data => 
            {return data}
            )
        .catch(function(error) {
        console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
        });
        console.log("data2",data2)
        setPanieritems(data2)
    } 

    return (
        <div className="commandeStep">
            <h4 className="commandStepTitre">
                Panier
            </h4>
            <div className="commandeStepContenu pb-5">
                <div className="panierRecap">
                    {
                        u!='rien trouvé'
                        ?
                            panierItems[0]!='' 
                            ?
                            produits
                            :
                            ""
                        :
                            panierItems[0]!='' 
                            ?
                            produits
                            :
                            ""

                    }
                </div>
                {
                    modif 
                    ?
                        props.button
                    :
                        ""
                }
            </div>
        </div>
    );
}

export default Panier;
