import React, { useState } from 'react';
import email from "../assets/img/email.jpg"
import erreur from "../assets/img/erreur.jpg"
import $ from 'jquery';

const Contact = (props) => {
    const [error,setError] = useState("")
    const contact_send = async (e) => {
        e.preventDefault();
        let inputs = new Object()
        for(let i=0;i<$(".inputform").length;i++)
          inputs[$(".inputform")[i].name] = $(".inputform")[i].value
        var inputsForm = JSON.stringify(inputs);
        console.log(inputsForm)
        var url = "http://localhost:8000/contact/"
        let data = await fetch(url,{
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                  }, 
                  body: inputsForm,
              })
              .then(response => {
                if(response.ok) {
                    console.log("fetchLogin",response)
                    return response.json();
                } 
                else {
                    console.log(response,'Mauvaise réponse du réseau');
                }
            })
            .then(data => {
                    return data
                })
            .catch(function(error) {
                console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
            });
            console.log(data)
            if (data=="félicitation"){
                props.setModalcontent(e,
                <div className='d-flex flex-column justify-content-center align-items-center'>
                    <h3>Merci de vous être inscrit à notre newsletter</h3>
                    <p>Vous recevrez par mail les promotions, nouveautés et disponibilités des produits Cam Securite</p>
                    <img src={email} alt="" style={{maxWidth:'400px',objectFit:'contain',}} />
                    
                </div>
                )
            }
            else{
                props.setModalcontent(e,
                <div className='d-flex flex-column justify-content-center align-items-center'>
                    <h3 className='text-danger'>Merci mais vous ètes déja inscrit à notre newsletter</h3>
                    <p>Vous recevrez par mail les promotions, nouveautés et disponibilités des produits Cam Securite</p>
                    <img src={erreur} alt="" style={{maxWidth:'400px',objectFit:'contain',}} />
                    
                </div>)
            }

    }
    return (
        <div>
            
          <form action="" className="formclassName">
              <h3 className='text-center' >Formulaire de Contact</h3>
            <div className="formrow ">
              <input id="in1" type="text" name='objet' className="inputform" placeholder=" " required/>
              <label for="in1" className="labelform">Titre*</label> 
            </div>
            <div className="formrow ">
              <textarea id="in2" type="text" name='message' className="inputform" placeholder=" " rows="5" required/>
              <label for="in2" className="labelform">Message*</label> 
            </div>
          <div className="formrow w-100">
            <input id="in3" type="email" name='email' className="inputform" placeholder=" " required/>
            <label for="in3" className="labelform">Email*</label> 
          </div>

          <p className="text-danger">
              {error}
          </p>
          <button className="formbtn" onClick={(e) => contact_send(e)}>
            Envoyer
          </button>
        </form>
        </div>
    );
}

export default Contact;
