import React, {useEffect, useState}  from 'react';
import Panier from './Panier';

const Commanderecap = (props) => {
    const [u,setU] = useState({id:0,user:{first_name:"",last_name:""}})
    const [panier,setPanier] = useState('')
    const [sousTotal,setSoustotal] = useState('')
    const [total,setTotal] = useState('')
    const [livraison,setLivraison] = useState(null)
    console.log('u u 168161',u)
    const getCSRF = async () => {
    var url2 = "http://localhost:8000/csrf/"
    let r = '' 
    await fetch(url2, {
        credentials: 'include',
      }).then(response => {
        if(response.ok) {
            console.log("fetchCSRF")
            r = response.json();
        } else {
            console.log(response,'Mauvaise réponse du réseau');
        }
        })
        .catch(function(error) {
        console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
        });
    return r
    }

    console.log('props ppp produit',props)
    useEffect(async () => {
        let t = async () => {
            var url = "http://localhost:8000/current_user/?format=json"
            let r = await getCSRF()
            const csrftoken = r.csrfToken
            let data = await fetch(url,{                
              headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
              'X-CSRFToken': csrftoken
            }, 
            credentials: 'include',
            }).then(response => {
            if(response.ok) {
                console.log("current_user ttt",response)
                return response.json();
            } else {
                console.log(response,'Mauvaise réponse du réseau');
            }
            }).then(data => 
                {
                  return(data)
                }
                )
            .catch(function(error) {
              console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
            });
            setU(data)
          }
          await t()
          if(u.id!=0){
              var url2 = `http://localhost:8000/choix-livraison/${u.id}/`
    
              let data2 = await fetch(url2).then(response => {
              if(response.ok) {
                  console.log("fetch livraison")
                  return response.json();
              } else {
                  console.log(response,'Mauvaise réponse du réseau');
              }
              }).then(data => 
                  {return data}
                  )
              .catch(function(error) {
              console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
              });
              console.log("data livraison 1222541455",data2)
              setLivraison(data2)
          }
          if(u.id!=0){
              var url2 = `http://localhost:8000/panier/${u.id}/`
    
              let data2 = await fetch(url2).then(response => {
              if(response.ok) {
                  console.log("fetch")
                  return response.json();
              } else {
                  console.log(response,'Mauvaise réponse du réseau');
              }
              }).then(data => 
                  {return data}
                  )
              .catch(function(error) {
              console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
              });
              console.log("88888888888888888888888888",data2)
              setPanier(data2[0].panier)
              let st = 0
              for (let i = 0; i<data2.lenght ; i++)
              {
                st = st + data2[i].prix
              }
              setSoustotal(st)
              setTotal(st + data2[0].panier.livraison.prix)
              console.log("ddata1151",st)
          }
        return () => {
        };
    }, [u.id]);
    const prods =[
        {
            produit:{
                nom:"",
                sousnom:"",
                prix:25,
                stock:10,
                image:"",
            },
            quantite:2,
            modif:false
        }
    ]
    const commande =(
        props.command
        ?
            props.commande
        :
            {
                user:{
                    prenom:"",
                    nom:"",
                    email:"",
                    adresse:"",
                    ville:"",
                    code_postal:"",
                    pays:"",
                    telephone:""
                },
                livraison:{
                    nom:"",
                    description:"",
                    prix:0,
                    delai:"",
                },
                sousTotal:250,
                total:300
            }
    )
    return (
        <div className="commandeStep w-100">
            <h4 className="commandStepTitre">
                Récapitulatif de la commande
            </h4>
            <div className="commandeStepContenu pb-5">
                <div className="d-flex flex-column align-items-center justify-content-center">
                    <div className="w-100 d-flex flex-column flex-md-row align-items-start justify-content-center">
                        <Panier produit={prods} modif={false} />
                        {
                            u.id != 0
                            ?
                            <div className="commandeLivraison d-flex flex-column">
                                <span className="fw-bold fs-4 mb-3">
                                    Détails de la livraison
                                </span>
                                <div className="d-flex mb-1">
                                    <span className="me-2">À</span>
                                    <span className="fw-bold">{u.user.prenom} {u.user.nom}</span>
                                </div>
                                <div className="d-flex mb-1">
                                    <span className="me-2">Adresse</span>
                                    <span className="fw-bold">{u.adresse}</span>
                                </div>
                                <div className="d-flex mb-1">
                                    <span className="me-2">Ville</span>
                                    <span className="fw-bold">{u.ville}</span>
                                </div>
                                <div className="d-flex">
                                    <span className="me-2">Code postal</span>
                                    <span className="fw-bold">{u.code_postal}</span>
                                </div>
                                <div className="d-flex">
                                    <span className="me-2">Pays</span>
                                    <span className="fw-bold">{u.pays}</span>
                                </div>
                                <div className="d-flex">
                                    <span className="me-2">Livré par</span>
                                    <span className="fw-bold">{livraison.nom}</span>
                                </div>
                                <div className="d-flex">
                                    <span className="me-2">Délai estimé</span>
                                    <span className="fw-bold">{livraison.delai}</span>
                                </div>
                            </div>
                            :
                            ""
                        }
                    </div>
                    {
                        panier.promo 
                        ?
                        <div className="d-flex align-items-center w-100 ms40">
                            <span className='me-2'>Le code promo <span className="fw-bold"> {panier.codePromo.nom} </span> est utilisé pour cette commande</span>
                            <a className=" ms-1">
                                Supprimer
                            </a>
                        </div>
                        :
                        <div className="d-flex align-items-center w-100 ms40">
                            <div className="formrow me-2">
                                <input id="in1" type="text" className="inputform" placeholder=" " required/>
                                    <label for="in1" className="labelform">Code promo</label>
                            </div>
                            <button className="produitBuyBtn ms-2">
                                Valider
                            </button>
                        </div>                        
                    }
  
                    <div className="d-flex flex-column ms40 w-100 mb-4">
                        <div className="d-flex mb-1">
                            <span className="me-2 fs-4">Sous-total</span>
                            <span className="fw-bold fs-4"> {sousTotal} €</span>
                        </div>
                        <div className="d-flex mb-1">
                            <span className="me-2 fs-4">Montant livraison</span>
                            <span className="fw-bold fs-4">{panier.livraison.prix} €</span>
                        </div>
                        <div className="d-flex mb-1">
                            <span className="me-2 fs-3 fw-bold" >Total commande</span>
                            <span className="fw-bolder fs-3" >{total} €</span>
                        </div>
                    </div>
                    {props.button}
                </div>

            </div>
        </div>
    );
}

export default Commanderecap;
