import React, {useEffect, useState}  from 'react';

const Gestioncommandes = (props) => {
    const getcommandes= async () =>{
        var url = "http://localhost:8000/commandes/"

        let data = await fetch(url).then(response => {
        if(response.ok) {
            return response.json();
        } else {
            console.log(response,'Mauvaise réponse du réseau');
        }
        }).then(data => 
            {
                return data
            }
            )
        .catch(function(error) {
        console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
        });
        return data
    }
    const [commandes_show, setcommandes_show] = useState([]);
    const [commandes, setcommandes] = useState([]);
    useEffect(async() => {
        const c = await getcommandes()
        console.log("cccccccccccccccccccccc",c)
        setcommandes(c)
        setcommandes_show(c.map(commande =>(
            <div className="commande position-relative align-items-center commandeAnim" >
                <div className="d-flex flex-column w-100 align-items-center">
                    <img src={commande.image1} alt="" className="accueilcommandeImage"/>
                    <span className="accueilcommandeNom">
                        {commande.nom}
                    </span>
                    <p className="accueilcommandeSousNom">
                        {commande.sousNom}
                    </p>
                </div>
                <div className="d-flex flex-column align-items-center">
                    <div className="d-flex">
                        {
                            commande.prixSansReduction!=commande.prix
                            ?
                            <del className="accueilcommandePrixBarre me-1">
                                {commande.prixSansReduction} €
                            </del>
                            :
                            ""                         
                        }
                        <span className="accueilcommandePrix ms-1 text-black">
                            {commande.prix} €
                        </span>                    
                    </div>
                </div>
            </div>
        )))
        return () => {
        };
    }, []);
    return (
        <div className='w-100 p-3' >
            <h1 className='fs-3' >Gestion des commandes</h1>
            <div className="d-flex align-items-center justify-content-between pe-3">
                <div className="d-flex align-items-center">
                    <div className="d-flex flex-column me-3">
                        <span className="fs-2"></span>
                        <span>Toutes les commandes</span>
                    </div>
                    <div className="d-flex flex-column me-3">
                        <span className="fs-2"></span>
                        <span>Commandes non traités</span>
                    </div>
                    <div className="d-flex flex-column me-3">
                        <span className="fs-2"></span>
                        <span>Commandes expediées</span>
                    </div>
                    <div className="d-flex flex-column me-3">
                        <span className="fs-2"></span>
                        <span>Commandes finis</span>
                    </div>
                </div>
            </div>
            <div className="d-flex flex-row justify-content-start align-items-center">
                {commandes_show}
            </div>

        </div>
    );
}

export default Gestioncommandes;
