import React from 'react';
import $ from 'jquery';

const Choixlivraison = (props) => {
    const choisirLivraison = (e) =>{
        $(".livChoix").removeClass("livChoixActive")
        e.target.classList.add("livChoixActive")
    }
    const modeLivraisons = (
        props.livraisons
        ?
            props.livraison.map(l =>
                <div id={l.id} class=" livChoix d-flex flex-column justify-content-center align-items-center p-3">
                    <img src={l.logo} alt="" class="livraisonImg"/>
                        <span class="livraisonPrix my-2">
                            {l.prix}
                        </span>
                        <p class="livraisonText">
                            {l.description}
                        </p>
                </div>
            )
        :
            <div id="0" class=" livChoix d-flex flex-column justify-content-center align-items-center p-3" onClick={(e) => choisirLivraison(e)}>
                <img src="" alt="" class="livraisonImg"/>
                    <span class="livraisonPrix my-2">
                        50,50 â‚¬
                    </span>
                    <p class="livraisonText">
                        Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolorem, consectetur?
                    </p>
            </div>
        )
    return (
        <div class="commandeStep w-100">
            <h4 class="commandStepTitre">
                Choix de la livraison
            </h4>
            <div class="commandeStepContenu pb-5">
                <div class="livraisonChoix d-flex justify-content-center">
                    {modeLivraisons}
                </div>
                <div class="w-100 d-flex align-items-center justify-content-center">
                    <button class="produitBuyBtn">Choisir ce mode de livraison</button>
                </div>
            </div>
        </div>
    );
}

export default Choixlivraison;
