import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Ampoulecamera = () => {
    const [produit_json, setProduit] = useState("")
    const [categorie, setCategorie] = useState("")
    useEffect(async() => {
        console.log('produit definit')
        var url = "http://localhost:8000/produit/1"

        let d = await fetch(url).then(response => {
            if(response.ok) {
                console.log("fetch")
                return response.json();
            } 
            else {
                console.log(response,'Mauvaise réponse du réseau');
            }
            }).then(data =>{
                return(data)
            })
            .catch(function(error) {
            console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
            });
            setProduit(d)
            setCategorie(d.categorie)
            console.log('ddddddddddddddddddddddddddaaa0153',d.categorie)
        
    }, []);

    var w = window.innerWidth,
    h = window.innerHeight,
    canvas = document.getElementById('canvasAmpoule'),
    ctx = canvas.getContext('2d'),
    rate = 60,
    arc = 100,
    time,
    count,
    size = 7,
    speed = 20,
    parts = new Array,
    colors = ['red','#f57900','yellow','#ce5c00','#5c3566'];
var mouse = { x: 0, y: 0 };

canvas.setAttribute('width',w);
canvas.setAttribute('height',h);

function create() {
  time = 0;
  count = 0;

  for(var i = 0; i < arc; i++) {
    parts[i] = {
      x: Math.ceil(Math.random() * w),
      y: Math.ceil(Math.random() * h),
      toX: Math.random() * 5 - 1,
      toY: Math.random() * 2 - 1,
      c: colors[Math.floor(Math.random()*colors.length)],
      size: Math.random() * size
    }
  }
}

function particles() {
  ctx.clearRect(0,0,w,h);
   canvas.addEventListener('mousemove', MouseMove, false);
  for(var i = 0; i < arc; i++) {
    var li = parts[i];
    var distanceFactor = DistanceBetween( mouse, parts[i] );
    var distanceFactor = Math.max( Math.min( 15 - ( distanceFactor / 10 ), 10 ), 1 );
    ctx.beginPath();
    ctx.arc(li.x,li.y,li.size*distanceFactor,0,Math.PI*2,false);
    ctx.fillStyle = li.c;
    ctx.strokeStyle=li.c;
    if(i%2==0)
      ctx.stroke();
    else
      ctx.fill();
    
    li.x = li.x + li.toX * (time * 0.05);
    li.y = li.y + li.toY * (time * 0.05);
    
    if(li.x > w){
       li.x = 0; 
    }
    if(li.y > h) {
       li.y = 0; 
    }
    if(li.x < 0) {
       li.x = w; 
    }
    if(li.y < 0) {
       li.y = h; 
    }
   
     
  }
  if(time < speed) {
    time++;
  }
  setTimeout(particles,1000/rate);
}
function MouseMove(e) {
   mouse.x = e.layerX;
   mouse.y = e.layerY;

   //context.fillRect(e.layerX, e.layerY, 5, 5);
   //Draw( e.layerX, e.layerY );
}
function DistanceBetween(p1,p2) {
   var dx = p2.x-p1.x;
   var dy = p2.y-p1.y;
   return Math.sqrt(dx*dx + dy*dy);
}
create();
particles();


    return (
        <div className="accueilBannInv w-100 position-relative" style={{background : "black"}}>
            <canvas id="canvasAmpoule"></canvas>
            {
                produit_json=='1'
                ?
                <div className="loader" id="loader-2"></div>
                :
                <img src={produit_json.image1} className="bannImgInv" />

            }
            <h2 className="bannTitre" style={{color : 'white'}}>
                Ampoule Caméra
            </h2>
            <h4 className="bannSoustitre" style={{color : 'white'}}>
                Prennez de la hauteur et surveillez vos pièces en 360° 💡🎥
            </h4>
            <div className="d-flex justify-content-center align-items-center w-100">
                {

                        produit_json!=undefined || produit_json!=""
                        ?
                        <Link to={`/produit/${produit_json.id}`}  className="bannLink bannBtn">
                            Acheter {produit_json.nom} 
                        </Link>                    
                        :
                        <div className="loader" id="loader-2"></div>
                }
                {
                        produit_json!=undefined || produit_json!=""
                        ?
                        <Link to={`/categorie/${categorie.id}`}  className="bannLink text-center">
                            Voir les {categorie.nom} 
                        </Link>                    
                        :
                        <div className="loader" id="loader-2"></div>
                }

            </div>
        </div>
    );
}

export default Ampoulecamera;
