import React, { Component } from 'react';
import { Link } from "react-router-dom";

import { signInWithPopup, GoogleAuthProvider } from "firebase/auth";
import googleLogo from "../assets/icon/google-logo.png"
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from '../../firebase_config'
import $ from 'jquery'
import { async } from '@firebase/util';

class Signup extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
          validationEmail: "",
          validationPassword: ""
        };
      this.inputs = React.createRef();
      this.inputs.current = []

      this.addInputs = async (el) => {
        if (el && !this.inputs.current.includes(el)) {
        this.inputs.current.push(el);
        }
        //this.inputs.Current.push(el)
        console.log(this.inputs.current)
      };
      this.formRef = React.createRef();

      this.setValidationEmail = (msg) => {
          this.setState({
              validationEmail: msg
          })
      }

      this.setValidationPassword = (msg) => {
          this.setState({
              validationPassword: msg
          })
      }

      this.signWithGoogle = async (e) => {
          //e.preventDefault();
          const provider = new GoogleAuthProvider();
          provider.setCustomParameters({
            'allow_signup': 'false'
          });
          console.log(auth)
          await signInWithPopup(auth, provider)
            .then((result) => {
                console.log(result)
              // This gives you a Google Access Token. You can use it to access the Google API.
              const credential = GoogleAuthProvider.credentialFromResult(result);
              const token = credential.accessToken;
              // The signed-in user info.
              const user = result.user;
              // ...
            }).catch((error) => {
                console.log(error)
              // Handle Errors here.
              const errorCode = error.code;
              const errorMessage = error.message;
              // The email of the user's account used.
              const email = error.email;
              // The AuthCredential type that was used.
              const credential = GoogleAuthProvider.credentialFromError(error);
              // ...
            });
         
      };

      this.signUp = async (e) => {
        e.preventDefault();
        const signingUp = (email, pwd) => createUserWithEmailAndPassword(auth, email, pwd)

        if((this.inputs.current[1].value.length || this.inputs.current[2].value.length) < 8) {
            this.setValidationPassword("8 characters min")
            return;
          }
        else if(this.inputs.current[1].value !== this.inputs.current[2].value) {
            this.setValidationPassword("Passwords do not match")
            return;
        }
      
          try {
      
            const cred = await signingUp(
              this.inputs.current[0].value,
              this.inputs.current[1].value
            )
      
          } catch (err) {
            console.log(err)
            if(err.code === "auth/invalid-email") {
              this.setValidationEmail("Email format invalid")
            }
      
            if(err.code === "auth/email-already-in-use") {
              this.setValidationEmail("Email already used")
            }
      
          }
      }
    }
    render() {
        return (
            <div className="vh-100 vw-100 m-0 p-0 overflow-x-hidden">
        <div id="loader" className="loader">
          <span className="loaderPoint"></span>
          <span className="loaderPoint"></span>
          <span className="loaderPoint"></span>
        </div>
        <div className="row h-100 rounded rounded-5 justify-content-end">
          <div
            id="three"
            className="col-md-6 vh-100 position-fixed start-0"
            style={{ backgroundImage: `url(${bg})`, backgroundSize: "cover" }}
          >
            <div
              id="carouselExampleControls"
              className="carousel slide p-5 w-100 h-100"
              data-bs-ride="carousel"
            >
              <div className="carousel-inner" role="listbox">
                <div className="carousel-item active">
                  <img src="" alt="First slide"></img>
                </div>
                <div className="carousel-item">
                  <img src="" alt="Second slide"></img>
                </div>
                <div className="carousel-item">
                  <img src="" alt="Third slide"></img>
                </div>
              </div>
              <div className="position-absolute start-50 bottom-0 translate-middle mb-5 d-flex">
                <button
                  className="rounded rounded-circle p-3 mx-2 border-0 bg-secondary text-light icon d-flex justify-content-center align-items-center carouselBtn"
                  type="button"
                  data-bs-target="#carouselExampleControls"
                  data-bs-slide="prev"
                >
                  <FontAwesomeIcon icon={faArrowLeft} />
                </button>
                <button
                  className="rounded rounded-circle p-3 mx-2 border-0 bg-secondary text-light icon d-flex justify-content-center align-items-center carouselBtn"
                  type="button"
                  data-bs-target="#carouselExampleControls"
                  data-bs-slide="next"
                >
                  <FontAwesomeIcon icon={faArrowRight} />
                </button>
              </div>
            </div>
          </div>
            <div
                className="col-md-6 d-flex flex-column justify-content-center align-items-center"
                style={{
                    padding: "70px",
                    paddingLeft: "200px",
                    paddingRight: "200px",
                }}
            >
                <Link className="position-absolute start-50 top-0 m-5" to="/">
                    Back To Home
                </Link>
                <h1 className="w-100 text-center fw-bold"> Sign up to Clever </h1> 
                <div className="d-flex justify-content-center my-3">
                    <div
                        onClick={this.signWithGoogle}
                        className="rounded rounded-5 bg-light p-4 m-2 icon-container"
                    >
                        <img src={googleLogo} className="icon" alt="" />
                    </div> 
                </div> 
                <div
                    className="w-100 d-flex justify-content-center align-items-center my-4 mb-0"
                    style={{
                        height: "2px",
                        background: "#ddd",
                    }}
                >
                    <small className="bg-white m-0 px-3 py-2">
                         
                        or do it via E-mail 
                    </small> 
                </div> 
                <form onSubmit={this.signUp} className="m-0 p-0 w-100">
                    <label
                        htmlFor="email"
                        className="text-capitalize fw-bold d-flex flex-column w-100 my-4"
                    >
                        Email
                        <input
                            id='email'
                            name='email'
                            type="text"
                            className={"rounded rounded-4 mt-1 p-3 text-secondary bg-light border-0 text-secondary formInput" }
                            placeholder="adresse@mail.com"
                            ref={this.addInputs}
                            required
                        />
                        <small id='emailValidation' className='text-danger'>{ this.state.validationEmail }</small>
                    </label>
                    <label
                        htmlFor="password"
                        className="text-capitalize fw-bold d-flex flex-column w-100 my-4"
                    >
                        Password
                        <input
                            type="password"
                            className="rounded rounded-4 mt-1 p-3 text-secondary bg-light border-0 text-secondary formInput"
                            placeholder="password"
                            ref={this.addInputs}
                            required
                        />
                        <small className="text-secondary fw-bold mt-1">
                            Must be 8 character at least
                        </small>
                    </label>
                    <label
                        htmlFor="passwordConfirmation"
                        className="text-capitalize fw-bold d-flex flex-column w-100 my-4"
                    >
                        Password Confirmation
                        <input
                            type="password"
                            className="rounded rounded-4 mt-1 p-3 text-secondary bg-light border-0 text-secondary formInput"
                            placeholder="password confirmation"
                            ref={this.addInputs}
                            required
                        /> 
                        <small id='emailValidation' className='text-danger'>{ this.state.validationPassword }</small>
                    </label>
                    <div className="d-flex justify-content-between">
                    <label
                        htmlFor="firstname"
                        className="text-capitalize fw-bold d-flex flex-column w-100 m-2 mb-4 ms-0"
                    >
                        Firstname
                        <input
                            type="text"
                            className="rounded rounded-4 mt-1 p-3 text-secondary bg-light border-0 text-secondary formInput"
                            placeholder="firstname"
                            ref={this.addInputs}
                            required
                        />
                    </label>
                    <label
                        htmlFor="lastname"
                        className="text-capitalize fw-bold d-flex flex-column w-100 m-2 mb-4 me-0"
                    >
                        Lastname
                        <input
                            type="text"
                            className="rounded rounded-4 mt-1 p-3 text-secondary bg-light border-0 text-secondary formInput"
                            placeholder="lastname"
                            ref={this.addInputs}
                            required
                        />
                    </label>
                    </div>
                    <label
                        htmlFor="birthDate"
                        className="text-capitalize fw-bold d-flex flex-column w-100 mb-4"
                    >
                        Birth Date
                        <input
                            type="date"
                            className="rounded rounded-4 mt-1 p-3 text-secondary bg-light border-0 text-secondary "
                            placeholder="xx/xx/xxxx"
                            ref={this.addInputs}
                            required
                        />
                    </label>
                    <div className="d-flex justify-content-between w-100 my-4">
                        <div className="form-check">
                            <input
                                type="checkbox"
                                className="form-check-input"
                                name=""
                                id=""
                                value="checkedValue"
                                ref={this.addInputs}
                                required
                            />
                            <label className="form-check-label" htmlFor="">
                                I accept the term of the uct
                            </label> 
                        </div> 
                        <a href="" className="text-primary text-capitalize fw-bold">
                            using condition
                        </a>
                    </div>
                    <button className="btn btn-primary w-100 my-4 rounded rounded-5" type='submit'>
                        Sign up
                    </button>
                </form>
                <div className="w-100 d-flex justify-content-center">
                    <Link className="text-center" to="/signin">
                        Already registered ?
                    </Link>
                </div>
                <small className="text-center text-secondary my-4 text-capitalize">
                    @2021 all right reserved 
                </small> 
            </div>
            </div>
            </div>
        );
    }
}

export default Signup;
