import React from 'react';

const Post = (props) => {
	const post = [
		{
				complete:{
					description:"Défi relevé avec brio, belle gosse pour fluncher !",
					photo1:"",
					photo2:"",
					photo3:"",
				},
				detail:[
					{
						categorie:"",
						photo:"",
						prix:25.95,
						marque:""
					},
					{
						categorie:"",
						photo:"",
						prix:25.95,
						marque:""
					},
					{
						categorie:"",
						photo:"",
						prix:25.95,
						marque:""
					},
					{
						categorie:"",
						photo:"",
						prix:25.95,
						marque:""
					},
				],
				defi:
				{
					titre:"Chic en blanc pour aller fluncher",
					semaine:"24 au 30 janvier 2022",
					date_debut:"24/01/2022",
					date_fin:"30/01/2022",
					regle:[
						{
							nom:"Budjet",
							valeur:"200 €"
						},
						{
							nom:"Type de commerce",
							valeur:"Boutique physique et en ligne"
						},
						{
							nom:"Delai",
							valeur:"Soumettre sa tenu avant la date de fin"
						},
					],
					participantNb:3,
					fini:false,
				},
				user:
				{
					prenom:"Nasser",
					nom:"Nourou",
					photo:"https://img-4.linternaute.com/ROUIuiso_K2p5B8_xuA9GZJ41CY=/2610x/smart/41072375ec4f4bae808550106194e814/ccmcms-linternaute/10656232.jpg",
					displayName:"Nasser Nourou",
					username:"nan0u_b1nks",
					dateCreation:"le 3 janvier 2022",
					points:2352,
					status:"Morphologie de type B majuscule",
					resaux:[
						{
							nom:"Instagram",
							identifiant:"@nanou-B1nks",
							url:"200 €"
						},
						{
							nom:"Tweeter",
							identifiant:"@nanou-B1nks",
							url:"Boutique physique et en ligne"
						},
						{
							nom:"Snapchat",
							identifiant:"@nanou-B1nks",
							url:"Soumettre sa tenu avant la date de fin"
						},
						{
							nom:"TikTok",
							identifiant:"@nanou-B1nks",
							url:"Soumettre sa tenu avant la date de fin"
						},
		
					],
					proprietaire:true,
				},
				prix:230.55,
				like:29,
				note_et_commentaire:[
					{
						note:8,
						commentaire:"",
						user:
						{
							prenom:"Nasser",
							nom:"Nourou",
							photo:"https://img-4.linternaute.com/ROUIuiso_K2p5B8_xuA9GZJ41CY=/2610x/smart/41072375ec4f4bae808550106194e814/ccmcms-linternaute/10656232.jpg",
							displayName:"Nasser Nourou",
							username:"nan0u_b1nks",
							dateCreation:"le 3 janvier 2022",
							points:2352,
							status:"Morphologie de type B majuscule",
							resaux:[
								{
									nom:"Instagram",
									identifiant:"@nanou-B1nks",
									url:"200 €"
								},
								{
									nom:"Tweeter",
									identifiant:"@nanou-B1nks",
									url:"Boutique physique et en ligne"
								},
								{
									nom:"Snapchat",
									identifiant:"@nanou-B1nks",
									url:"Soumettre sa tenu avant la date de fin"
								},
								{
									nom:"TikTok",
									identifiant:"@nanou-B1nks",
									url:"Soumettre sa tenu avant la date de fin"
								},
				
							],
							proprietaire:true,
						}
					}
				],
				note_generale:7.75,
				commentaire_nbre:1,
				date:"il y a 1 jour"

		}
	]

	const posts = post.map(p => 
		<div className="d-flex flex-column align-items-center w-100 mb-3 bg-white rounded rounded-5 p-3">
			<div className="d-flex flex-column justify-content-between align-items-ceer w-100 mb-2">
				<h1 className="defiName mb-2">
					{p.defi.titre}
				</h1>
				<div className="d-flex align-items-center mt-3">
					<span className="rounded rounded-5 me-2 bg-dark storyProfile"style={{backgroundImage:`url(${p.user.photo})`}}></span>
					<div className="d-flex flex-column ">	
						<strong className="text-secondary text-capitalize ps-2">{p.user.username}</strong>
						<span className="text-secondary ps-2">{p.date}</span>
					</div>
				</div>
				<h4 className="mt-3">
					{p.complete.description}
				</h4>
			</div>
			<div className="container-fluid p-1 postContent position-relative">
				<div className="likeContain">
					<div className="d-flex justify-content-end">
						<div className="close" onClick={(e) => {
							e.target.parentElement.parentElement.classList.remove("act") 
								}}></div>
					</div>
				</div>
				<div className="commentContain">
					<div className="d-flex justify-content-end">
						<div className="close" onClick={(e) => {
							e.target.parentElement.parentElement.classList.remove("act") 
								}}></div>
					</div>
				</div>
				<div className="mediaContain">
					<div className="quitZone" onClick={(e) => {
							e.target.parentElement.classList.remove("act") 
								}}></div>
					<div className="media"></div>
				</div>
				<div className="row">
					<div className="col-lg-10 postImgContainer">
						<img src="" alt="" className="postImg" />
					</div>
					<div className="col-lg-2 d-flex flex-column align-items-center postImgContainerBis">
						{
							p.detail.map(d =>
								<div className="postDetail">
									<span className="mb-3">
										{d.categorie}
									</span>
									<img src={d.photo} alt="" className="postImgBis" />
								</div>
							)
						}
					</div>
				</div>
				<div className="d-flex justify-content-between align-items-center w-100 mb-2 p-1">
					<div className="d-flex align-items-center">
						<span className="d-flex align-items-center comment" onClick={(e) => {
							console.log(e.target.parentElement.parentElement.parentElement.children[1])
							e.target.parentElement.parentElement.parentElement.children[1].classList.contains("act") ?
							e.target.parentElement.parentElement.parentElement.children[1].classList.remove("act"):
							console.log("fermer")
							setTimeout(() => e.target.parentElement.parentElement.parentElement.children[0].classList.add("act"),500)
								}}>
							<i className="far fa-heart fa-2x me-2"></i>
							{p.commentaireNb}
						</span>
						<span className="ms-4 d-flex align-items-center" onClick={(e) => { 
							console.log(e.target.parentElement.parentElement.parentElement.children[0])
							e.target.parentElement.parentElement.parentElement.children[0].classList.contains("act") ?
							e.target.parentElement.parentElement.parentElement.children[0].classList.remove("act"):
							console.log("fermer")
							setTimeout(() => e.target.parentElement.parentElement.parentElement.children[1].classList.add("act"),500)
						}}>
							<i className="far fa-comment fa-2x me-2"></i>
							{p.likeNb}
						</span>
					</div>
					<i className="fas fa-share px-2 fa-2x"></i>
				</div>
			</div>
			
			
		</div>
	)
    return (
        <div className="">		
				{posts}
			</div>
    );
}

export default Post;
