import React from 'react';

function Defi(props){
	const defis_json = [
		{
			titre:"Chic en blanc pour aller fluncher",
			semaine:"24 au 30 janvier 2022",
			date_debut:"24/01/2022",
			date_fin:"30/01/2022",
			regle:[
				{
					nom:"Budjet",
					valeur:"200 €"
				},
				{
					nom:"Type de commerce",
					valeur:"Boutique physique et en ligne"
				},
				{
					nom:"Delai",
					valeur:"Soumettre sa tenu avant la date de fin"
				},
			],
			participantNb:3,
			fini:false,
		},
		{
			titre:"Moderne en Desigual",
			semaine:"17 au 23 janvier 2022",
			date_debut:"17/01/2022",
			date_fin:"23/01/2022",
			regle:[
				{
					nom:"Budjet",
					valeur:"250 €"
				},
				{
					nom:"Type de commerce",
					valeur:"Boutique physique uniquement"
				},
				{
					nom:"Contrainte d'achat",
					valeur:"au moin une pièce de la marque Desigual"
				},
				{
					nom:"Delai",
					valeur:"Soumettre sa tenu avant la date de fin"
				},
			],
			participantNb:3,
			fini:true,
		},
		{
			titre:"Sexy pour voler la vedette au mariage de ta meilleure amie",
			semaine:"10 au 16 janvier 2022",
			date_debut:"10/01/2022",
			date_fin:"16/01/2022",
			regle:[
				{
					nom:"Budjet",
					valeur:"350 €"
				},
				{
					nom:"Type de commerce",
					valeur:"Boutique physique et en ligne"
				},
				{
					nom:"Delai",
					valeur:"Soumettre sa tenu avant la date de fin"
				},
			],
			participantNb:3,
			fini:true,
		},
	]

	const defi = defis_json.map((d) =>
		<div className='w-100'>
			{
				(d.fini===false) ?
				<div className="defi">
					<h4 className="defiDate">
						Défi de la semaine du {d.semaine}	
					</h4>				
					<h3 className="defiName">
						{d.titre}
					</h3>
					<div className="d-flex align-items-center">
						<ul className="defiRegleList">
							{d.regle.map(r => 
							<li className="defiRegle">
								<span>{r.nom} :</span>
								<span>{r.valeur}</span>
							</li>	
							)}
						</ul>
						<div className="defiGain">

						</div>
					</div>
					<div className="d-flex align-items-center">
						<button className="defiBtn">Participer au defi</button>
						<button className="defiBtn">Voir le Showroom</button>
						<p className="defiParticipant">
							déja {d.participantNb} participants pour ce défi
						</p>
					</div>
				</div>
				:
				<div className="defi fini">
					<h4 className="defiDate">
						Défi de la semaine {d.semaine}	
					</h4>				
					<h3 className="defiName">
						{d.titre}
					</h3>
					<div className="d-flex align-items-center">
						<button className="defiBtn">Voir le Showroom</button>
						<p className="defiParticipant">
							{d.participantNb} ont participé à ce défi
						</p>
					</div>
				</div>
			}
		</div>
	)

    return (
		<div className=" d-flex flex-column justify-content-center align-items-center ">
			{defi}
		</div>  
    );
}
export default Defi;
