import { auth } from '../firebase_config';
import Navbar from "./components/Navbar";
import Compte from "./components/Compte";
import Defi from "./components/Defi";
import Post from "./components/Post";
import Amis from "./components/Amis";
import Menu from "./components/Menu";
import React, { useState } from 'react';
import { doc, setDoc, getFirestore, collection, query, where, getDocs, } from "firebase/firestore"; 

console.log(auth.currentUser)

function Accueilconnexion(props) {

    const [amis, setAmis]= useState([]);
    const initAmis = async (s) => {
        let d =[]
        const db = getFirestore();
        const r = collection(db, "friends");
        const q = await query(r, where("email1", "==", auth.currentUser.email));
        console.log('q=',q)
        const querySnapshot = await getDocs(q);
        querySnapshot.forEach((doc) => {
            // doc.data() is never undefined for query doc snapshots
              d.push(doc.data())
          });
          console.log('d=',d)
          s(d)
        }

    const [demande, setDemande]= useState([]);
    const initDemande = async (s) => {
        let d =[]
        const db = getFirestore();
        const r = collection(db, "request");
        const q = await query(r, where("email2", "==", auth.currentUser.email));
        console.log('q=',q)
        const querySnapshot = await getDocs(q);
        querySnapshot.forEach((doc) => {
            // doc.data() is never undefined for query doc snapshots
              d.push(doc.data())
          });
          console.log('d=',d)
          s(d)
        }


    return (
        <div>
                <div className="w-100 d-flex justify-content-center">
                    <aside className="p-0 d-none d-lg-block start-0 vh-100 d-flex align-items-center sideLeft">
                        <Menu elchange={props.elchange} el={props.el}/>
                    </aside>
                    <div className=" p-5 mainCente middle">
                        {props.el}
                    </div>
                    <aside className="sideRight p-2 d-none d-lg-flex end-0 vh-100 d-flex justify-content-center">
                            <Compte/>
                    </aside>
                </div>
        </div>
    );
}

export default Accueilconnexion;
