import React from 'react';
import { doc, setDoc, getFirestore, collection, query, where, getDocs, deleteDoc } from "firebase/firestore"; 
import uuid from 'react-uuid'
import { auth } from '../../firebase_config';


function Demande(props) {
    const initDemande = () => {
        console.log("p=",props)
        //const di = props.initdemande(props.setdemande)
        console.log("di=",props.demande)
    }
    const beFriend = async (email) =>{
        const db = getFirestore();
        const e = async (email) => {
            console.log("ok")
            await setDoc(doc(db, "friends", uuid() ), {
                email1: auth.currentUser.email,
                email2: email,
              });
            await deleteDoc(doc(db, "request", where("email1", "==", email) , where("email2", "==", auth.currentUser.email)));
            }
          e(email)
    }
    var demandes = props.demande.map((d) =>
    <div key={d.email1} className="rounded rounded-5 p-2 bg-white d-flex flex-column mb-2">
        <div className="d-flex align-items-center">
            <span className="rounded rounded-5 me-2 bg-dark storyProfile "></span>
            <span className="text-secondary">
                <strong className="text-capitalize">{d.email1}</strong> veut t'ajouter a ses amis
            </span>
        </div>
        <div className="d-flex py-2">
            <button className="btn bg-dark rounded rounded-5 text-uppercase text-light me-3" onClick={() => beFriend(d.email1)}>accepter</button>
            <button className="btn border border-2 border-dark rounded rounded-5 text-uppercase text-dark me-3">refuser</button>
        </div>
    </div>
    )
    var nb = props.demande.length
    return (            			
			<div className="bg-light rounded rounded-5 p-2 mb-2 w-100">
				<div className="d-flex justify-content-between align-items-center">
					<span className="text-secondary text-uppercase p-2" onLoadStart={initDemande()} >demandes</span>
					<span className="bg-danger p-2 m-2 rounded-circle text-light notifNumber text-center d-flex align-items-center justify-content-center">{nb}</span>
				</div>
				<div className="w-100 d-flex flex-column gap-y-2">
                    {demandes}
				</div>
			</div>
    );
}

export default Demande;
