import React from "react";
import { auth } from "../../firebase_config";
import logo from "../assets/img/rds.png"
import accueil from "../assets/icon/home.png"
import defi from "../assets/icon/defi.png"
import showroom from "../assets/icon/showroom.png"
import note from "../assets/icon/note.png"
import tips from "../assets/icon/tips.png"

function Menu(props){
    const navItems = [
        {
            nom:"Accueil",
            icon:accueil,
            action:"",
            active:true,
            notification:0
        },
        {
            nom:"Les défis",
            icon:defi,
            action:"",
            active:"",
            notification:0
        },
        {
            nom:"Mon showroom",
            icon:showroom,
            action:"",
            active:"",
            notification:0
        },
        {
            nom:"Mes notes",
            icon:note,
            action:"",
            active:"",
            notification:0
        },
        {
            nom:"Aide",
            icon: tips,
            action:"",
            active:"",
            notification:0
        }
    ]

    const items = navItems.map(it => 
        <li className="list-style-none p-2 ps-0 d-flex justify-content-between tr" onClick={it.action}>
            <div className=" menuIt w-100 py-2 d-flex justify-content-between tr">
                <div className="d-flex align-items-center tr mx-2 w-100">
                    <img src={it.icon} alt="" className="icon"/>
                    <span className={`text-uppercase ms-2 menuName tr me-2 " ${it.active ? "text-danger" : "text-secondary"}`}>
                    {it.nom}
                    </span>
                </div>
                <span className={`bg-danger p-2 m-2  text-light pill rounded-pill badge text-center d-flex align-items-center justify-content-center ${ it.notification==0 ? "d-none" : ""}`}>
                    {it.notification}
                </span>
            </div>
        </li>
    )
  return (
    <div className="w-100 d-flex flex-column align-items-center h-100 position-relative">
        <img src={logo} alt="" className="logo" />
        <div className="w-100 d-flex align-items-center h-100">
          <ul className="p-1 py-3 leftMenu" >
            { items }
          </ul>
      </div>
    </div>
  );
};

export default Menu;
