import {
    PaymentElement,
    useStripe,
    useElements
  } from "@stripe/react-stripe-js";
import React, {useState} from "react";
import ApiService from "../components/api.js";
const Stripe = () => {
  const [error, setError] = useState(null);
  const [email, setEmail] = useState('');
  const stripe = useStripe();
  const elements = useElements();
// Handle real-time validation errors from the CardElement.
const handleChange = (event) => {
  if (event.error) {
    setError(event.error.message);
  } else {
    setError(null);
  }
}
// Handle form submission.
const handleSubmit = async (event) => {
  event.preventDefault();
  const card = elements.getElement(PaymentElement);
  const {paymentMethod, error} = await stripe.createPaymentMethod({
        type: 'card',
        card: card
    });
    ApiService.saveStripeInfo({
    email, payment_method_id: paymentMethod.id})
    .then(response => {
    console.log(response.data);
    }).catch(error => {
    console.log(error)
})
};

return (
  <form onSubmit={handleSubmit} className="stripe-form">
    <div className="form-row">
          <div className="formrow w-100">
            <input id="email" type="email" name='name' className="inputform" placeholder="" value={email} onChange={(event) => { setEmail(event.target.value)}}  required/>
            <label for="in3" className="labelform">Email*</label> 
          </div>
    </div>
    <div className="form-row">
      <label for="card-element">Carte de crédit ou de débit</label> 
      <PaymentElement id="payment-element" onChange={handleChange}/>
      <div className="card-errors" role="alert">{error}</div>
    </div>
    <button type="submit" className="submit-btn">
      Submit Payment
    </button>
  </form>
 );
};
export default Stripe;
