                                var url2 = "http://localhost:8000/commande-success/"
                                let r = await getCSRF()
                                const csrftoken = r.csrfToken
                                let data = await fetch(url2,{
                                        method: 'POST',
                                        headers: {
                                            'Accept': 'application/json',
