import React, { Component } from 'react';
import Navbar from "./components/Navbar";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRight, faArrowLeft } from '@fortawesome/free-solid-svg-icons'
import bg from "./assets/img/rules-bg.jpg"
import Login from './components/login';
import SignUp from './components/signup';
import Home from './components/home'
import { BrowserRouter, Routes, Route, } from "react-router-dom";
import Accueilconnexion from './AccueilConnexion';
import { auth } from '../firebase_config'
console.log("auth:",auth)

class Accueil extends Component {
    constructor(props){
        super(props);
        this.state = {
            user:auth.currentUser!=null ? auth.currentUser :"",
            userName:auth.currentUser!=null ? auth.currentUser.displayName :"", 
        }
        
    }
    userChange = (u) =>{
        this.setState({
            user: u!=null ? u.currentUser :"",
            userName: u!=null ? u.displayName : ""
        })
        console.log("okkk")
}
    render() {
        let el = <Home user={ this.state.user }/> 
        if (auth.currentUser != null){
            el =<Accueilconnexion user={ this.state.user} username={ this.state.userName } userchange={ this.userChange } />
        }
        return (
            <>  
                <Navbar user={ this.state.user} username={ this.state.userName } userchange={ this.userChange } />
                <Routes>
                    <Route path="/" element={ el } />
                    <Route path="/signup" element={ <SignUp user={ this.state.user} username={ this.state.userName } userchange={ this.userChange }/> } />
                    <Route path="/signin" element={ <Login user={ this.state.user} username={ this.state.userName } userchange={ this.userChange }/> } />
                    <Route path="/dashboard" element={ <Accueilconnexion user={ this.state.user } username={ this.state.userName } userchange={ this.userChange }/> } />
                </Routes>
            </>
        );
    }
}




export default Accueil;
