import React from 'react';

const Banniereproduitaccueil = (props) => {
    return (
        <div className="accueilBann position-relative" style={{background : props.background,backgroundSize:"cover",backgroundRepeat:"no-repeat"}}>
            <div id="loader" className="loadIT three col">
                <div className="loader" id="loader-1"></div>
            </div>
            <h2 className="bannTitre" style={{color : props.titreColor}}>
                {props.titre}
            </h2>
            <h4 className="bannSoustitre" style={{color : props.sousTitreColor}}>
                {props.sousTitre}
            </h4>
            <div className="d-flex justify-content-center align-items-center w-100">
                {
                    props.acheter==false 
                    ?
                    ""
                    :
                    <a  className="bannLink bannBtn">
                        Acheter 
                    </a>
                }
                {
                    props.categorie==false 
                    ?
                    ""
                    :
                    <a className="bannLink">
					    Voir les 
                    </a>
                }

            </div>
            <img src={props.image} className="bannImg" style={{objectPosition : props.position, width : props.size}}/>
        </div>
    );
}

export default Banniereproduitaccueil;
