					p.comment.map(l => 
					<div className="d-flex align-items-start justify-content-center flex-column m-4 p-3 com">
						<div className="d-flex align-items-center">
							<span className="rounded rounded-5 me-2 bg-dark storyProfile"style={{backgroundImage:`url(${l.user.photo})`}}></span>
							<div className="d-flex flex-column ">	
								<strong className="text-dark text-capitalize ps-2">{l.user.username}</strong>
								<span className="text-secondary ps-2">{l.user.points} points</span>
								<span className="text-secondary ps-2">{l.date}</span>
							</div>
						</div>
						<div className="d-flex align-items-center mt-3">
                            {
                                l.note 
                                ?
                                <div className="note p-2 px-4 ">
                                    <span className='fs-4'>
                                        {l.note}
                                    </span>
                                    <span className="trait"></span>
                                    <span className='fs-4'>
