import React, {useEffect, useState}  from 'react';
import $ from 'jquery';

const Choixlivraison = (props) => {
    const [u,setU] = useState({id:0,user:{first_name:"",last_name:""}})
    const [livraison,setLivraison] = useState(null)
    console.log('u u 168161',u)
    const getCSRF = async () => {
    var url2 = "http://localhost:8000/csrf/"
    let r = '' 
    await fetch(url2, {
        credentials: 'include',
      }).then(response => {
        if(response.ok) {
            console.log("fetchCSRF")
            r = response.json();
        } else {
            console.log(response,'Mauvaise réponse du réseau');
        }
        })
        .catch(function(error) {
        console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
        });
    return r
    }

    console.log('props ppp produit',props)
    useEffect(async () => {
        let t = async () => {
            var url = "http://localhost:8000/current_user/?format=json"
            let r = await getCSRF()
            const csrftoken = r.csrfToken
            let data = await fetch(url,{                
              headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
              'X-CSRFToken': csrftoken
            }, 
            credentials: 'include',
            }).then(response => {
            if(response.ok) {
                console.log("current_user ttt",response)
                return response.json();
            } else {
                console.log(response,'Mauvaise réponse du réseau');
            }
            }).then(data => 
                {
                  return(data)
                }
                )
            .catch(function(error) {
              console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
            });
            setU(data)
            return data
          }
          let us = await t()
          if(us!='rien trouvé'){
              var url2 = `http://localhost:8000/choix-livraison/${u.id}/`
    
              let data2 = await fetch(url2).then(response => {
              if(response.ok) {
                  console.log("fetch livraison")
                  return response.json();
              } else {
                  console.log(response,'Mauvaise réponse du réseau');
              }
              }).then(data => 
                  {return data}
                  )
              .catch(function(error) {
              console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
              });
              console.log("data livraison 1222541455",data2)
              setLivraison(data2)
          }
          else{
            var url2 = `http://localhost:8000/choix-livraison-nolog/`
  
            let data2 = await fetch(url2).then(response => {
            if(response.ok) {
                console.log("fetch livraison")
                return response.json();
            } else {
                console.log(response,'Mauvaise réponse du réseau');
            }
            }).then(data => 
                {return data}
                )
            .catch(function(error) {
            console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
            });
            console.log("data livraison 1222541455",data2)
            setLivraison(data2)
            
          }
        return () => {
        };
    }, [u.id]);
    const choisirLivraison = (e) =>{
        $(".livChoix").removeClass("livChoixActive")
        e.target.classList.add("livChoixActive")
    }
    const modeLivraisons = (
        livraison != null
        ?
            livraison.map(l =>
                <div className="liv d-flex flex-column justify-content-center align-items-center p-4 position-relative" onClick={(e) => choisirLivraison(e)}>
                    <img src={l.type.image} alt="" className="livraisonImg" style={{objectFit:"contain"}} />
                        <span className="livraisonPrix my-2">
                            {l.prix} €
                        </span>
                        <p className="livraisonText">
                            Délai de livraison: {l.type.delai} 
                        </p>
                    <div id={l.id} className="livChoix position-absolute top-0 bottom-0 start-0 end-0"></div>
                </div>
            )
        :
            ""
        )
    return (
        <div className="commandeStep w-100">
            <h4 className="commandStepTitre">
                Choix de la livraison
            </h4>
            <div className="commandeStepContenu pb-5">
                <div className="livraisonChoix d-flex justify-content-center">
                    {modeLivraisons}
                </div>
                {props.button}
            </div>
        </div>
    );
}

export default Choixlivraison;
