        let t = async () => {
            var url = "http://localhost:8000/current_user/?format=json"
            let r = await getCSRF()
            const csrftoken = r.csrfToken
            let data = await fetch(url,{                
              headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
              'X-CSRFToken': csrftoken
            }, 
            credentials: 'include',
            }).then(response => {
            if(response.ok) {
                console.log("current_user ttt",response)
                return response.json();
            } else {
                console.log(response,'Mauvaise réponse du réseau');
            }
            }).then(data => 
                {
                  return(data)
                }
                )
            .catch(function(error) {
              console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
            });
            setU(data.user)
          }
          await t()
          if(u.id!=undefined){
              var url2 = `http://localhost:8000/panier/${u.id}/`
    
              let data2 = await fetch(url2).then(response => {
              if(response.ok) {
                  console.log("fetch")
                  return response.json();
              } else {
                  console.log(response,'Mauvaise réponse du réseau');
              }
              }).then(data => 
                  {return data}
                  )
              .catch(function(error) {
              console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
              });
              setPanieritems(data2)
              setPanier(data2[0].panier)
              console.log("ddata1151",panierItems)
          }
        return () => {
        };
    }, [u.id]);
    const user = (
        props.user
        ?
            props.user
        :
            {
                prenom:"",
                nom:"",
                email:"",
                adresse:"",
                ville:"",
                code_postal:"",
                pays:"",
                telephone:""
            }
    )
    return (

        <div class="commandeStep w-100">
            <h4 class="commandStepTitre">
                Informations livraison
            </h4>
            <div class="commandeStepContenu pb-5">
                <div class="livraison">

                    <div class="d-flex justify-content-between align-items-center w-100">
                        <div class="formrow me-2">
                            <input id="in1" type="text" class="inputform" value={user.prenom} required/>
                                <label for="in1" class="labelform">Prénom*</label>
                        </div>
                        <div class="formrow ms-2">
                            <input id="in2" type="text" class="inputform" value={user.nom}  required/>
                                <label for="in2" class="labelform">Nom*</label>
                        </div>
                    </div>
                    <div class="formrow w-100">
                        <input id="in3" type="email" class="inputform" value={user.email}  required/>
                            <label for="in3" class="labelform">Email*</label>
                    </div>

                    <div class="formrow w-100">
                        <input id="in6" type="text" class="inputform" value={user.adresse}  required/>
                            <label for="in6" class="labelform">Adresse*</label>
                    </div>

                    <div class="d-flex justify-content-between align-items-center w-100">
                        <div class="formrow me-2">
                            <input id="in7" type="text" class="inputform " value={user.ville}  required/>
                                <label for="in7" class="labelform">Ville*</label>
                        </div>
                        <div class="formrow ùs-2">
                            <input id="in8" type="text" class="inputform " value={user.code_postal}  required/>
                                <label for="in8" class="labelform">Code postal*</label>
                        </div>
                    </div>

                    <div class="d-flex justify-content-between align-items-center w-100">
                        <div class="formrow me-2">
                            <input id="in9" type="text" class="inputform " value={user.pays}  required/>
                                <label for="in9" class="labelform">Pays*</label>
                        </div>
                        <div class="formrow ms-2">
                            <input id="in10" type="text" class="inputform " value={user.telephone}  required/>
                                <label for="in10" class="labelform">Téléphone</label>
                        </div>
                    </div>


                </div>
                {props.button}
            </div>
        </div>
    );
}

export default Livraison;
