	const changePhoto = (p) =>{
		const el = "c"+p.id
		const index = document.getElementById(el)
		let i = index.innerHTML
		let newi = 0
		console.log("p",p)
		if (Number(i)===p.img.length-1){
			newi = 0
		}
		else{
			newi = Number(i)+1
		}
		index.innerHTML = newi
		index.parentElement.children[1].src = p.img[newi]
	}

