import Accueil from './pages/Accueil';
import React, { useState, useEffect } from 'react';
import './App.scss';
import { auth } from './firebase_config'
import Navtop from './pages/components/Navtop';
import Produitslist from './pages/components/ProduitsList';
import Stepbarre from './pages/components/StepBarre';
import Panier from './pages/components/Panier';
import Livraison from './pages/components/Livraison';
import Choixlivraison from './pages/components/ChoixLivraison';
import Commanderecap from './pages/components/CommandeRecap';
import Panierprocess from './pages/PanierProcess';
import $ from 'jquery';
import { BrowserRouter, Route, Routes } from "react-router-dom";
import Footer from './pages/components/Footer';
import Produitachat from './pages/components/ProduitAchat';
import Compte from './pages/Compte';


function App(props) {
  let cat = {
    nom: "Caméra Espion",
    sousNom: "Très vicieux",
    banniereImage: "https://images.unsplash.com/photo-1512790182412-b19e6d62bc39?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1332&q=80",
  }
  const [modalContent, setModalcontent] = useState();
  const [token,setToken] = useState(localStorage.getItem('token') ? true : false)
  const [logged_in,setLogin] = useState(token)
  const [user, setUser] = useState('');

  var url = "http://localhost:8000/current_user/?format=json"
  fetch(url,{
    headers: {
      Authorization: `JWT ${localStorage.getItem('token')}`
    }}).then(response => {
  if(response.ok) {
      console.log("current_user",response)
      return response.json();
  } else {
      console.log(response,'Mauvaise réponse du réseau');
  }
  }).then(data => 
      {
          console.log("reponse",data)
          localStorage.setItem('token', data.token);
      }
      )
  .catch(function(error) {
  console.log('Il y a eu un problème avec l\'opération fetch: ' + error.message);
  });

  const showHideModal = (e, modal) => {
    console.log('ls',localStorage.getItem('token'))

    e.preventDefault()
    if ($(".modalContainer").hasClass("d-none")) {
      setModalcontent(modal)
      $(".modalContainer").removeClass("d-none")
      $("body").addClass('noScroll')
    }
    else {
      $(".modalContainer").addClass("d-none")
      $("body").removeClass('noScroll')
    }
  }

  useEffect(() => {
    // Update the document title using the browser API
      $(".loadIT").addClass("o0")
      setTimeout(()=>{$(".loadIT").addClass("d-none")},1000)
      console.log("0")

  });

  return (
    <>
      <div id="loader" className="loadIT three col">
        <div className="loader" id="loader-1"></div>
      </div>
      <BrowserRouter>
      <Navtop showHideModal={showHideModal} modalContent={modalContent} setModalcontent={setModalcontent} user={user} token={token} setToken={setToken} logged_in={logged_in} setLogin={setLogin} />
        <Routes>
          <Route path="/" element={<Accueil showHideModal={showHideModal} modalContent={modalContent} setModalcontent={setModalcontent} />} />
          <Route path="/panier/:id" element={<Panierprocess showHideModal={showHideModal} modalContent={modalContent} setModalcontent={setModalcontent} />} />
          <Route path="/categorie/:id" element={<Produitslist showHideModal={showHideModal} modalContent={modalContent} setModalcontent={setModalcontent} />} />
          <Route path="/produit/:id" element={<Produitachat showHideModal={showHideModal} modalContent={modalContent} setModalcontent={setModalcontent} />} />
          <Route path="/compte/:id" element={<Compte showHideModal={showHideModal} modalContent={modalContent} setModalcontent={setModalcontent} user={user} token={token} setToken={setToken} logged_in={logged_in} setLogin={setLogin} />} />
          <Route path="/recherche/:s" element={<Produitslist showHideModal={showHideModal} modalContent={modalContent} setModalcontent={setModalcontent} />} />
        </Routes>
      </BrowserRouter>
      <Footer/>

    </>
  );

}

export default App;
