import React, {useEffect} from 'react';
import Navtop from './components/Navtop';
import Banniereproduitaccueil from './components/BanniereProduitAccueil';
import banniereReveil from './assets/img/banniereReveil.png'
import banniereAmpoule from './assets/img/banniereAmpoule.png'
import BanniereproduitaccueilInv from './components/BanniereProduitAccueilInv';
import Bannieretopproduits from './components/BanniereTopProduits';

const Accueil = (props) => {

    function disparitionHorizontal(el){
      el.classList.add("disparitionHorizontal");
      el.classList.remove("apparitionHorizontal");
      setTimeout(hide(el),1000);
    } 
    function apparitionHorizontal(el){
        setTimeout(()=>{
          visible(el);
          el.classList.add("apparitionHorizontal");
          el.classList.remove("disparitionHorizontal");
      },1000)
    } 
    
    function hide (el){
      el.classList.add("hide");
    }
    function visible (el){
      el.classList.remove("hide");
    }
    
    const sleep = (ms) =>
      new Promise(resolve => setTimeout(resolve, ms));
    
    
    useEffect(() => {
        let offre1 = document.getElementById("offre1");
        let offre2 = document.getElementById("offre2");
        let offre3 = document.getElementById("offre3");
        setInterval(() => {
            disparitionHorizontal(offre3)
            apparitionHorizontal(offre1)
        }, 15000);
        
        setInterval(() => {
            setTimeout(() => {                
                disparitionHorizontal(offre1)
                apparitionHorizontal(offre2)
            }, 5000);
        }, 10000);
        setInterval(() => {
            setTimeout(() => {                
                disparitionHorizontal(offre2)
                apparitionHorizontal(offre3)
            }, 10000);
        }, 5000);
    })
    
    return (
        <div className="w-100 main">

            <div class="caouselOffre">
                <div id="offre1" class="offre hide">
                    <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Sint, dignissimos!</p>
                </div>
                <div id="offre2" class="offre hide">
                    <p>Lorem ipsum dolor </p>
                </div>
                <div id="offre3" class="offre">
                    <p>Lorem ipsum dolor sit amet consectetur adipisicing</p>
                </div>
            </div>

            <Banniereproduitaccueil titre="Réveil caméra" sousTitre="Surveiller en toute discrétion" titreColor="" sousTitreColor="" background="linear-gradient(120deg, #fdfbfb 0%, #ebedee 100%)" image={banniereReveil} />
            <BanniereproduitaccueilInv titre="Ampoule caméra" sousTitre="Surveiller vos pièces" titreColor="white" sousTitreColor="white" background="linear-gradient(to right, #434343 0%, black 100%)" image={banniereAmpoule} />
            <Banniereproduitaccueil titre="Réveil caméra" sousTitre="Surveiller en toute discrétion" titreColor="" sousTitreColor="" background="linear-gradient(120deg, #fdfbfb 0%, #ebedee 100%)" image={banniereReveil} />
            <Bannieretopproduits titre="Meilleures ventes" />
            <div className="d-flex w-100 gap-2 flex-column flex-md-row">
                <Banniereproduitaccueil titre="Micro espion" sousTitre="Surveiller en toute discrétion" titreColor="" sousTitreColor="" background="linear-gradient(120deg, #fdfbfb 0%, #ebedee 100%)" image={banniereReveil} />
                <BanniereproduitaccueilInv titre="Kit caméra de surveillance" sousTitre="Surveiller vos pièces" titreColor="white" sousTitreColor="white" background="linear-gradient(to right, #434343 0%, black 100%)" image={banniereAmpoule} />
            </div>
            <div className="d-flex w-100 gap-2 flex-column flex-md-row">
                <Banniereproduitaccueil titre="Webcam" sousTitre="Surveiller en toute discrétion" titreColor="" sousTitreColor="" background="linear-gradient(120deg, #fdfbfb 0%, #ebedee 100%)" image={banniereReveil} />
                <Banniereproduitaccueil titre="Stockage" sousTitre="Surveiller en toute discrétion" titreColor="white" sousTitreColor="white" background="linear-gradient(to right, #ff758c 0%, #ff7eb3 100%)" image={banniereReveil} />
            </div>

            <div className="d-flex w-100 justify-content-start justify-content-md-center align-items-center p-2 mw-100 overflow-auto my-2">
                <div className="d-flex flex-column p-2 m-2 align-items-center justify-content-between" style={{height:"200px"}}>
                    <img src="" alt="" className="m-2 mb-1" style={{width:"100px",height:"100px"}}/>
                    <span className="m-2 mt-1">Expédition en 24 heure seulement</span>
                </div>
                <div className="d-flex flex-column p-2 m-2 align-items-center justify-content-between" style={{height:"200px"}}>
                    <img src="" alt="" className="m-2 mb-1" style={{width:"100px",height:"100px"}}/>
                    <span className="m-2 mt-1">Stock en France</span>
                </div>
                <div className="d-flex flex-column p-2 m-2 align-items-center justify-content-between" style={{height:"200px"}}>
                    <img src="" alt="" className="m-2 mb-1" style={{width:"100px",height:"100px"}}/>
                    <span className="m-2 mt-1">Paiement sécurisé</span>
                </div>
                <div className="d-flex flex-column p-2 m-2 align-items-center justify-content-between" style={{height:"200px"}}>
                    <img src="" alt="" className="m-2 mb-1" style={{width:"100px",height:"100px"}}/>
                    <span className="m-2 mt-1">SAV disponible et réactif</span>
                </div>
            </div>
        </div>
    );
}
export default Accueil;
