import React, { Component } from 'react';
import Navbar from "./components/Navbar";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowRight, faArrowLeft } from '@fortawesome/free-solid-svg-icons'
import bg from "./assets/img/rules-bg.jpg"
import Login from './components/login';
import SignUp from './components/signup';
import Home from './components/home'
import { BrowserRouter, Routes, Route, } from "react-router-dom";
import Accueilconnexion from './AccueilConnexion';
import { auth } from '../firebase_config'
import Defi from "./components/Defi";
import Post from "./components/Post";
import Conseil from "./components/Conseil";
import Compte from './components/Compte';
console.log("auth:",auth)

class Accueil extends Component {
    constructor(props){
        super(props);
        this.state = {
            user:auth.currentUser!=null ? auth.currentUser :"",
            userName:auth.currentUser!=null ? auth.currentUser.displayName :"", 
            el:<Conseil elchange={this.elChange} comptechange={this.compteChange}/>,
            compte:<Compte/>,
        }
        
    }
    userChange = (u) =>{
        this.setState({
            user: u!=null ? u.currentUser :"",
            userName: u!=null ? u.displayName : "",
            userPP: u!=null ? u.photoURL : ""
        })
        console.log("okkk")
}
elChange = (el) =>{
    this.setState({
        el:el
    })
}

compteChange = (el) =>{
    this.setState({
        compte:el
    })
}
    render() {
        let el = <Home user={ this.state.user }/> 
        if (auth.currentUser != null){
            el =<Accueilconnexion user={ this.state.user} username={ this.state.userName } userchange={ this.userChange } />
        }
        return (
            <>  
                <Routes>
                    <Route path="/" element={ el } />
                    <Route path="/signup" element={ <SignUp user={ this.state.user} username={ this.state.userName } userpic={ this.state.userPP } userchange={ this.userChange }/> } />
                    <Route path="/signin" element={ <Login user={ this.state.user} username={ this.state.userName }  userpic={ this.state.userPP } userchange={ this.userChange }/> } />
                    <Route path="/dashboard" element={ <Accueilconnexion el={this.state.el} compte={this.state.compte} elchange={this.elChange} comptechange={this.compteChange} user={ this.state.user } username={ this.state.userName } userpic={ this.state.userPP } userchange={ this.userChange }/> } />
                </Routes>
            </>
        );
    }
}




export default Accueil;
