import React from 'react';

function Defi(props){
	const defis_json = [
		{
			titre:"",
			semaine:"",
			date_debut:"",
			date_fin:"",
			regle:[
				{
					nom:"",
					valeur:""
				},
				{
					nom:"",
					valeur:""
				},
				{
					nom:"",
					valeur:""
				},
			],
			participantNb:3,
			fini:false,
		},
	]

	const defi = defis_json.map(d =>
		{
			(d.fini===false) ?
			<div className="defi">
				<h4 className="defiDate">
					Défi de la semaine {d.semaine}	
				</h4>				
				<h3 className="defiName">
					{d.titre}
				</h3>
				<div className="d-flex align-items-center">
					<ul className="defiRegleList">
						{d.regle.map(r => 
						<li className="defiRegle">
							<span>{r.titre} :</span>
							<span>{r.valeur}</span>
						</li>	
						)}
					</ul>
					<div className="defiGain">

					</div>
				</div>
				<div className="d-flex align-items-center">
					<button className="defiBtn">Participer au defi</button>
					<button className="defiBtn">Voir le Showroom</button>
					<p className="defiParticipant">
						déja {d.participantNb} participent à ce défi
					</p>
				</div>
			</div>
			:
			<div className="defi">
				<h4 className="defiDate">
					Défi de la semaine {d.semaine}	
				</h4>				
				<h3 className="defiName">
					{d.titre}
				</h3>
				<div className="d-flex align-items-center">
					<button className="defiBtn">Voir le Showroom</button>
					<p className="defiParticipant">
						déja {d.participantNb} participent à ce défi
					</p>
				</div>
			</div>
		}
	)

    return (
		<div className=" d-flex flex-column justify-content-center align-items-center ">
				{defi}
		</div>  
    );
}

export default Defi;
