import React, { Component } from 'react';
import { signOut } from "firebase/auth";
import { auth } from '../../firebase_config'
import { Link, useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch} from '@fortawesome/free-solid-svg-icons'
import { doc, setDoc, getFirestore, collection, query, where } from "firebase/firestore"; 


function Navbar(props) {
    const navigate = useNavigate();
    const signingOut = (e) => {
        e.preventDefault();
        signOut(auth).then(() => {
            // Sign-out successful.
            props.userchange(auth.currentUser);
            console.log('bye')
            console.log(auth.currentUser)
            navigate("/")
        }).catch((error) => {
            // An error happened.
            console.log(error);
        });
    }
    let inputs = React.createRef();
    inputs.current = []
    const addInputs = async (el) => {
    if (el && !inputs.current.includes(el)) {
    inputs.current.push(el);
    }
    //inputs.Current.push(el)
    console.log(inputs.current)
    };
    const searchUser = async (e) =>{
        const db = getFirestore();
        const users = collection(db, "users");
        const q = await query(users, where("firstName", "==", inputs[0].value));
    }
    return (
        <nav id="navTop" className="navbar navbar-expand-lg navbar-light bg-light  mx-0 py-3 fixed-top">
        <div className="container-fluid d-flex flex-nowrap px-5">
        <a className="navbar-brand me-1" href="#">LOGO</a>
        <div className="d-flex w-100 align-items-center" id="">
            <div className="w-100 mx-lg-5 mx-1"> 
                    <div className="rounded-pill bg-white w-100 d-flex align-items-center pe-2 ps-2">	
                        <input className="form-control border-0 w-100 bg-transparent me-3" type="search" placeholder="Recherche" aria-label="Search" onChange={searchUser} ref={addInputs}/>
                        <FontAwesomeIcon icon={faSearch} />
                    </div>
            </div>
            <div className="d-flex align-items-center">
            <button className="btn btn-dark h-auto py-1 d-none d-lg-block mx-5" >Cr√©er une partie</button>
        <button className="btn btn-outline-light  p-3 rounded-circle profilBtn" style={{ backgroundImage: `url(${props.userpic})`}} type="button" id="dropdownMenuButton1" data-bs-toggle="dropdown" aria-expanded="false" >
                    </button>
                    <ul className="dropdown-menu end-0" aria-labelledby="dropdownMenuButton1">
                        <li className="d-flex flex-column justify-content-center align-items-center">
                            <div className="rounded-circle" ></div>	
                            <p className="text-center text-capitalize mt-2 mb-0">{props.username}</p>
                            <small className="">Lorem ipsum dolor sit amet consectetur.</small>
                        </li>
                        <li>
                            <a className="dropdown-item" href="#">Another action</a>
                        </li>
                        <li><a className="dropdown-item" href="#">Something else here</a></li>
                        
                        <li><a className="dropdown-item" onClick={signingOut}>Log off !</a></li>
                    </ul>
            </div>
        </div>
        </div>
        </nav>
          
    );
}

export default Navbar;
