                                        let pr = newp.map(p => <Produit className='produitAnim' user={props.user} setUser={props.setUser} id={p.id} nom={p.nom} sousNom={p.sousNom} prix={p.prix} prixSansReduc={p.prixSansReduction} image={p.image1} />)
                                        changeProduits(pr)
                                    }} />
                                    Non
                                </label>
                            </div>
                        </div>
                    </div>
                </ReactShadowScroll>
                <div className="containerProduits">
                    <div className="triContainer ">
                        <button className="filtreBtn d-md-none" onClick={() => showHideFiltre()} >Filtre</button>
                        <div className='d-flex flex-column flex-md-row'>
                            <div className="select" tabindex="1">
                                <input className="selectopt" name="test" type="radio" id="opt1" checked/>
                                <label for="opt1" class="option">Trier par</label>
                                <input className="selectopt" name="test" type="radio" id="opt2"/>
                                <label for="opt2" class="option"  
                                onClick={()=>{
                                    let p = produit_json
                                    p.sort(function (a, b) {
                                        if(a.vente<b.vente)
                                            return 1
                                        if(a.vente>b.vente)
                                            return -1
                                        return 0
                                    })
                                    console.log(p)
                                    setProduits(p)
                                    let pr = produit_json.map(p => <Produit className='produitAnim' user={props.user} setUser={props.setUser} id={p.id} nom={p.nom} sousNom={p.sousNom} prix={p.prix} prixSansReduc={p.prixSansReduction} image={p.image1} />)
                                    changeProduits(pr)
                                    gsap.fromTo(".produitAnim", {opacity: 0, scale:0, duration: 0.5},{opacity:1,scale:1});

                                    setTri("Actuellement trié par nombre de vente")
                                }}>Meilleures ventes</label>
                                <input className="selectopt" name="test" type="radio" id="opt3"/>
                                <label for="opt3" class="option" 
                                onClick={()=>{
                                    let p = produit_json
                                    p.sort(function (a, b) {
                                        if(a.date_ajout<b.date_ajout)
                                            return 1
                                        if(a.date_ajout>b.date_ajout)
                                            return -1
                                        return 0
                                    })
                                    console.log(p)
                                    setProduits(p)
                                    let pr = produit_json.map(p => <Produit className='produitAnim' user={props.user} setUser={props.setUser} id={p.id} nom={p.nom} sousNom={p.sousNom} prix={p.prix} prixSansReduc={p.prixSansReduction} image={p.image1} />)
                                    changeProduits(pr)
                                    gsap.fromTo(".produitAnim", {opacity: 0, scale:0, duration: 0.5},{opacity:1,scale:1});

                                    setTri("Actuellement trié par date d'ajout")
                                }}>Nouveautés</label>
                                <input className="selectopt" name="test" type="radio" id="opt4"/>
                                <label for="opt4" class="option" 
                                onClick={()=>{
                                    let p = produit_json
                                    p.sort(function (a, b) {
                                        if(a.prix>b.prix)
                                            return 1
                                        if(a.prix<b.prix)
                                            return -1
                                        return 0
                                    })
                                    console.log(p)
                                    setProduits(p)
                                    let pr = produit_json.map(p => <Produit className='produitAnim' user={props.user} setUser={props.setUser} id={p.id} nom={p.nom} sousNom={p.sousNom} prix={p.prix} prixSansReduc={p.prixSansReduction} image={p.image1} />)
                                    changeProduits(pr)
                                    gsap.fromTo(".produitAnim", {opacity: 0, scale:0, duration: 0.5},{opacity:1,scale:1});

                                    setTri("Actuellement trié par prix croissant")
                                }}>Prix croissants</label>
                                <input className="selectopt" name="test" type="radio" id="opt5"/>
                                <label for="opt5" class="option"
                                onClick={()=>{
                                    let p = produit_json
                                    p.sort(function (a, b) {
                                        if(a.prix>b.prix)
                                            return -1
                                        if(a.prix<b.prix)
                                            return 1
                                        return 0
                                    })
                                    console.log(p)
                                    setProduits(p)
                                    let pr = produit_json.map(p => <Produit className='produitAnim' user={props.user} setUser={props.setUser} id={p.id} nom={p.nom} sousNom={p.sousNom} prix={p.prix} prixSansReduc={p.prixSansReduction} image={p.image1} />)
                                    changeProduits(pr)
                                    gsap.fromTo(".produitAnim", {opacity: 0, scale:0, duration: 0.5},{opacity:1,scale:1});

                                    setTri("Actuellement trié par prix décroissant")
                                }}>Prix décroissants</label>
                            </div>
                            <span className='ms-2 fst-italic text-secondary'>{tri}</span>
                        </div>
                    </div>
                    <div className="produits">
                        {
                        produits.length>0
                        ?
                        produits.map(p => p)
                        :
                        "Aucun produit"
                        }
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Produitslist;
