import React from 'react';
import Produit from './Produit';
import $ from 'jquery';

const Produitslist = (props) => {
    console.log(props.categorie.banniereImage)
    const Background = props.categorie.banniereImage
    const categories = ""
    const produit_json = [
        {
            nom: "Caméra réveil",
            sousNom: "Voir sans être vu",
            prix: 165,
            image: "https://www.tomsguide.fr/content/uploads/sites/2/cache/2021/11/nest-indoor/3552683667.png",
        },
        {
            nom: "Caméra réveil",
            sousNom: "Voir sans être vu",
            prix: 165,
            image: "https://www.tomsguide.fr/content/uploads/sites/2/cache/2021/11/nest-indoor/3552683667.png",
        },
        {
            nom: "Caméra réveil",
            sousNom: "Voir sans être vu",
            prix: 165,
            image: "https://www.tomsguide.fr/content/uploads/sites/2/cache/2021/11/nest-indoor/3552683667.png",
        },
        {
            nom: "Caméra réveil",
            sousNom: "Voir sans être vu",
            prix: 165,
            image: "https://www.tomsguide.fr/content/uploads/sites/2/cache/2021/11/nest-indoor/3552683667.png",
        },
        {
            nom: "Caméra réveil",
            sousNom: "Voir sans être vu",
            prix: 165,
            image: "https://www.tomsguide.fr/content/uploads/sites/2/cache/2021/11/nest-indoor/3552683667.png",
        },
        {
            nom: "Caméra réveil",
            sousNom: "Voir sans être vu",
            prix: 165,
            image: "https://www.tomsguide.fr/content/uploads/sites/2/cache/2021/11/nest-indoor/3552683667.png",
        },
        {
            nom: "Caméra réveil",
            sousNom: "Voir sans être vu",
            prix: 165,
            image: "https://www.tomsguide.fr/content/uploads/sites/2/cache/2021/11/nest-indoor/3552683667.png",
        },
    ]
    const produits = produit_json.map(p => <Produit nom={p.nom} sousNom={p.sousNom} prix={p.prix} image={p.image} />)

    let showHideFiltre = () => {
        if($(".flitreContainer").hasClass("d-none")){
        console.log("okkk")
            $(".flitreContainer").removeClass("d-none")
            $("body").addClass('noScroll')
        }
        else{
            $(".flitreContainer").addClass("d-none")
            $("body").removeClass('noScroll')
        }
    }


    var x, i, j, l, ll, selElmnt, a, b, c;
    /* Look for any elements with the class "custom-select": */
    $(document).on('ready',() => {
        x = document.getElementsByClassName("custom-select");
        l = x.length;
        for (i = 0; i < l; i++) {
          selElmnt = x[i].getElementsByTagName("select")[0];
          x[i].remove()
          ll = selElmnt.length;
          /* For each element, create a new DIV that will act as the selected item: */
          a = document.createElement("div");
          a.setAttribute("className", "select-selected");
          a.innerHTML = selElmnt.options[selElmnt.selectedIndex].innerHTML;
          x[i].appendChild(a);
          /* For each element, create a new DIV that will contain the option list: */
          b = document.createElement("DIV");
          b.setAttribute("className", "select-items select-hide");
          for (j = 1; j < ll; j++) {
            /* For each option in the original select element,
            create a new DIV that will act as an option item: */
            c = document.createElement("DIV");
            c.innerHTML = selElmnt.options[j].innerHTML;
            c.addEventListener("click", function(e) {
                /* When an item is clicked, update the original select box,
                and the selected item: */
                var y, i, k, s, h, sl, yl;
                s = this.parentNode.parentNode.getElementsByTagName("select")[0];
                sl = s.length;
                h = this.parentNode.previousSibling;
                for (i = 0; i < sl; i++) {
                  if (s.options[i].innerHTML == this.innerHTML) {
                    s.selectedIndex = i;
                    h.innerHTML = this.innerHTML;
                    y = this.parentNode.getElementsByClassName("same-as-selected");
                    yl = y.length;
                    for (k = 0; k < yl; k++) {
                      y[k].removeAttribute("class");
                    }
                    this.setAttribute("class", "same-as-selected");
                    break;
                  }
                }
                h.click();
            });
            b.appendChild(c);
          }
          x[i].appendChild(b);
          a.addEventListener("click", function(e) {
            /* When the select box is clicked, close any other select boxes,
            and open/close the current select box: */
            e.stopPropagation();
            closeAllSelect(this);
            this.nextSibling.classList.toggle("select-hide");
            this.classList.toggle("select-arrow-active");
          });
        }
    })
    
    function closeAllSelect(elmnt) {
      /* A function that will close all select boxes in the document,
      except the current select box: */
      var x, y, i, xl, yl, arrNo = [];
      x = document.getElementsByClassName("select-items");
      y = document.getElementsByClassName("select-selected");
      xl = x.length;
      yl = y.length;
      for (i = 0; i < yl; i++) {
        if (elmnt == y[i]) {
          arrNo.push(i)
        } else {
          y[i].classList.remove("select-arrow-active");
        }
      }
      for (i = 0; i < xl; i++) {
        if (arrNo.indexOf(i)) {
          x[i].classList.add("select-hide");
        }
      }
    }
    
    /* If the user clicks anywhere outside the select box,
    then close all select boxes: */
    document.addEventListener("click", closeAllSelect);
    return (
        <>
        
        <div className="flitreContainer d-none">
                <div className="filtres w-100">
                    <div className=" w-100 d-flex flex-column p-3">
                        <span className="filtreTitre">Filtres</span>
                        <div className="filtre">
                            <span className="filtreNom">
                                Catégories
                            </span>
                            <ul className="categoriesList">
                                {categories}
                            </ul>
                        </div>
                        <div className="filtre">
                            <span className="filtreNom">
                                Connectivité
                            </span>
                            <div className="d-flex pl15">
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    WiFi
                                </label>
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    GSM
                                </label>
                            </div>
                        </div>
                        <div className="filtre">
                            <span className="filtreNom">
                                Prix
                            </span>
                            <div className="d-flex flex-wrap pl15 justify-content-between w-100">
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    0 - 50
                                </label>
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    51 - 100
                                </label>
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    101 - 150
                                </label>
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    151 - 200
                                </label>
                            </div>

                        </div>
                        <div className="filtre">
                            <span className="filtreNom">
                                Résolution
                            </span>
                            <div className="d-flex pl15">
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    HD
                                </label>
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    Full HD
                                </label>
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    4K
                                </label>
                            </div>

                        </div>
                    </div>
                </div>
                <div className="filtreQuitter" onClick={() => showHideFiltre() }>
                </div>
            </div>
            <div className='w-100 main'>
                
                <div className="categorieImageBann" style={{
                            backgroundImage: `url(${Background})`
                        }}>
                    <div className="blurContain bgblur"></div>
                    <div className="categorieBannText"
                        >
                        <h1 className="categorieBannTitre">
                            {props.categorie.nom}
                        </h1>
                        <h2 className="categorieBanSoustitre">
                            {props.categorie.sousNom}
                        </h2>
                    </div>
                </div>
                <div className="mainProduits ">
                    <div className="filtreProduits d-none d-md-flex">
                        <span className="filtreTitre">Filtres</span>
                        <div className="filtre">
                            <span className="filtreNom">
                                Catégories
                            </span>
                            <ul className="categoriesList">
                                {categories}
                            </ul>
                        </div>
                        <div className="filtre">
                            <span className="filtreNom">
                                Connectivité
                            </span>
                            <div className="d-flex pl15">
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    WiFi
                                </label>
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    GSM
                                </label>
                            </div>
                        </div>
                        <div className="filtre">
                            <span className="filtreNom">
                                Prix
                            </span>
                            <div className="d-flex flex-wrap pl15 justify-content-between w-100">
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    0 - 50
                                </label>
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    51 - 100
                                </label>
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    101 - 150
                                </label>
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    151 - 200
                                </label>
                            </div>

                        </div>
                        <div className="filtre">
                            <span className="filtreNom">
                                Résolution
                            </span>
                            <div className="d-flex pl15">
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    HD
                                </label>
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    Full HD
                                </label>
                                <label htmlFor="" className="filtreChoix">
                                    <input type="checkbox" className="me-2" />
                                    4K
                                </label>
                            </div>

                        </div>
                    </div>
                    <div className="containerProduits">
                        <div className="triContainer">
                            <button className="filtreBtn d-md-none" onClick={() => showHideFiltre() } >Filtre</button>
                            <div className="custom-select" style={{ width: '200px'}}>
                                <select>
                                    <option value="0">Trier les produits</option>
                                    <option value="1">Nouveauté</option>
                                    <option value="2">Meilleures ventes</option>
                                    <option value="3">Prix croissant</option>
                                </select>
                            </div>
                        </div>
                        <div className="produits">
                            {produits}
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Produitslist;
