import React, { useState } from "react";
import { auth } from "../../firebase_config";
import logo from "../assets/img/rds.png"
import actu from "../assets/icon/actu.png"
import defi from "../assets/icon/defi.png"
import showroom from "../assets/icon/showroom.png"
import amis from "../assets/icon/amis.png"
import tips from "../assets/icon/tips.png"
import Defi from "./Defi";
import Post from "./Post";
import Amis from "./Amis"
import Conseil from "./Conseil";
import Logout from "../assets/icon/logout.png"

function Menu(props){

    const navItems = [
        {
            nom:"Fil D'Actu",
            n:"Conseil",
            icon:actu,
            action:() => props.elchange(<Conseil elchange={props.elchange}/>),
            active:true,
            notification:0
        },
        {
            nom:"Défis",
            n:"Defi",
            icon:defi,
            action:() => {
                props.elchange(<Defi elchange={props.elchange}/>)
            },
            active:"",
            notification:0
        },
        {
            nom:"Mon Dress",
            n:"Post",
            icon:showroom,
            action:() => props.elchange(<Post/>),
            active:"",
            notification:0
        },
        {
            nom:"Amis",
            n:"Amis",
            icon: amis,
            action:() => props.elchange(<Amis/>),
            active:"",
            notification:0
        },
        {
            nom:"Bye",
            n:'',
            icon: Logout,
            action:"",
            active:"",
            notification:0
        }
    ]
    const [nav, setNav]= useState(navItems);
    console.log(props.el)

    const items = nav.map(it => 
        <li className="list-style-none p-2 ps-0 d-flex justify-content-between tr" onClick={it.action}>
            <div className=" menuIt w-100 py-2 d-flex justify-content-between tr">
                <div className="d-flex align-items-center tr mx-2 w-100">
                    <img src={it.icon} alt="" className="icon"/>
                    <span className={`text-uppercase ms-2 menuName tr me-2 " ${String(props.el.type.name)==it.n ? "text-danger" : "text-white"}`}>
                    {it.nom}
                    </span>
                </div>
                <span className={`bg-danger p-2 m-2  text-light pill rounded-pill badge text-center d-flex align-items-center justify-content-center ${ it.notification==0 ? "d-none" : ""}`}>
                    {it.notification}
                </span>
            </div>
        </li>
    )
  return (
    <div className="w-100 d-flex flex-column align-items-center h-100 position-relative bg-black">
        <img src={logo} alt="" className="logo" />
        <div className="w-100 d-flex align-items-center h-100">
          <ul className="p-1 py-3 leftMenu pe-3" >
            { items }
          </ul>
        </div>
    </div>
  );
};

export default Menu;
