import React from 'react';

const BanniereproduitaccueilInv = (props) => {
    return (
        <div className="accueilBannInv w-100" style={{background : props.background}}>
            {
                props.h=='100'
                ?
                <img src={props.image} className="bannImgInv h-100" />
                :
                <img src={props.image} className="bannImgInv" />

            }
            <h2 className="bannTitre" style={{color : props.titreColor}}>
                {props.titre}
            </h2>
            <h4 className="bannSoustitre" style={{color : props.sousTitreColor}}>
                {props.sousTitre}
            </h4>
            <div className="d-flex justify-content-center align-items-center w-100">
                {
                    props.acheter==false 
                    ?
                    ""
                    :
                        produit_json!=undefined || produit_json!=""
                        ?
                        <Link to={`/produit/${produit_json.id}`}  className="bannLink bannBtn">
                            Acheter {produit_json.nom} 
                        </Link>                    
                        :
                        ""
                }
                {
                    props.categorie==false 
                    ?
                    ""
                    :

                        produit_json!=undefined || produit_json!=""
                        ?
                        <Link to={`/categorie/${categorie.id}`}  className="bannLink ">
                            Voir les {categorie.nom} 
                        </Link>                    
                        :
                        ""
                }

            </div>
        </div>
    );
}

export default BanniereproduitaccueilInv;
