import { auth } from '../firebase_config';
import Navbar from "./components/Navbar";
import Demande from "./components/Demande";
import Story from "./components/Story";
import Post from "./components/Post";
import Amis from "./components/Amis";
import Menu from "./components/Menu";
import React, { useState } from 'react';
import { doc, setDoc, getFirestore, collection, query, where, getDocs, } from "firebase/firestore"; 

console.log(auth.currentUser)

function Accueilconnexion(props) {

    const [amis, setAmis]= useState([]);
    const initAmis = async (s) => {
        let d =[]
        const db = getFirestore();
        const r = collection(db, "friends");
        const q = await query(r, where("email1", "==", auth.currentUser.email));
        console.log('q=',q)
        const querySnapshot = await getDocs(q);
        querySnapshot.forEach((doc) => {
            // doc.data() is never undefined for query doc snapshots
              d.push(doc.data())
          });
          console.log('d=',d)
          s(d)
        }

    const [demande, setDemande]= useState([]);
    const initDemande = async (s) => {
        let d =[]
        const db = getFirestore();
        const r = collection(db, "request");
        const q = await query(r, where("email2", "==", auth.currentUser.email));
        console.log('q=',q)
        const querySnapshot = await getDocs(q);
        querySnapshot.forEach((doc) => {
            // doc.data() is never undefined for query doc snapshots
              d.push(doc.data())
          });
          console.log('d=',d)
          s(d)
        }


    return (
        <div>
            <Navbar user={ props.user} username={ props.username } userchange={ props.userchange } userpic={ props.userpic } />
            <div className="container-fluid main">
                <div className="row">
                    <aside className="col-lg-2 p-2 d-none d-lg-block">
                        <Menu/>
                    </aside>
                    <div className="col-lg-7 p-2 ps-mobile">
                        <Story />
                        <Post />
                    </div>
                    <aside className="col-lg-3 p-2 d-none d-lg-block">
                        <div className="w-100 px-4">
                            <Demande demande={demande} setdemande={setDemande} initdemande={initDemande} />
                            <Amis amis={amis} setamis={setAmis} initamis={initAmis} />
                        </div>
                    </aside>
                </div>
            </div>
        </div>
    );
}

export default Accueilconnexion;
