import React from 'react';

const Post = (props) => {
	const post = [
		{
			defi:false,
			user:{
				username:"NanouBinkz",
				pp:""
			},
			media:[
				{
					titre:"",
					url:""
				},
				{
					titre:"",
					url:""
				},
				{
					titre:"",
					url:""
				},
				{
					titre:"",
					url:""
				},
			],
			titre:"Un titre de post assez interressant, limite vulgaire pour faire plus de clique",
			date:"Il y a 4 heures",
			mediaNb:4,
			commentaire:[
				{

				}
			],
			commentaireNb:33,
			likeNb:134,

		},
		{
			defi:false,
			user:{
				username:"NanouBinkz",
				pp:""
			},
			media:[
				{
					titre:"",
					url:""
				},
				{
					titre:"",
					url:""
				},

			],
			titre:"Un titre de post assez interressant, limite vulgaire pour faire plus de clique",
			date:"Il y a 4 heures",
			mediaNb:2,
			commentaire:[
				{

				}
			],
			commentaireNb:33,
			likeNb:134,

		},
		{
			defi:false,
			user:{
				username:"NanouBinkz",
				pp:""
			},
			media:[
				{
					titre:"",
					url:""
				},

			],
			titre:"Un titre de post assez interressant, limite vulgaire pour faire plus de clique",
			date:"Il y a 4 heures",
			mediaNb:1,
			commentaire:[
				{

				}
			],
			commentaireNb:33,
			likeNb:134,

		},
		{
			defi:{
				titre:"Un titre de post assez interressant, limite vulgaire pour faire plus de clique",
				regle:[
					{
						titre:"budget",
						valeur:"Maximum 200 euros"
					}
				],
				dateFin:"20 février 2022",			},
			user:{
				username:"NanouBinkz",
				pp:""
			},
			date:"",
			likeNb:134,
			participantNb:33,

		},
	]

	const posts = post.map(p => 
		<div>

			{ (p.defi===false) ?
				<div className="d-flex flex-column align-items-center w-100 mb-3 bg-white rounded rounded-5 p-3">
					<div className="d-flex justify-content-between align-items-center w-100 mb-2">
						<div className="d-flex align-items-center">
							<span className="rounded rounded-5 me-2 bg-dark storyProfile"style={{backgroundImage:`url(${p.user.pp})`}}></span>
							<div className="d-flex flex-column ">	
								<strong className="text-secondary text-capitalize ps-2">{p.user.username}</strong>
								<span className="text-secondary ps-2">{p.date}</span>
							</div>
						</div>
						<i className="fas fa-angle-right px-2 fa-2x"></i>
					</div>
					<p className="p-1 text-dark m-0 pb-0 w-100 text-start">{p.titre}</p>
					<div className="container-fluid p-1 postContent position-relative">
						<div className="likeContain">
							<div className="d-flex justify-content-end">
								<div className="close" onClick={(e) => {
									e.target.parentElement.parentElement.classList.remove("act") 
										}}></div>
							</div>
						</div>
						<div className="commentContain">
							<div className="d-flex justify-content-end">
								<div className="close" onClick={(e) => {
									e.target.parentElement.parentElement.classList.remove("act") 
										}}></div>
							</div>
						</div>
						<div className="mediaContain">
							<div className="quitZone" onClick={(e) => {
									e.target.parentElement.classList.remove("act") 
										}}></div>
							<div className="media"></div>
						</div>
						{ (p.mediaNb===1) ?
							<div className="row w-100 h-100">
								<div className="col-12 p-2 pe-2 pb-1 " >
									<div className="rounded rounded-5 bg-dark w-100 h-100" onClick={(e) => {e.target.parentElement.parentElement.parentElement.children[2].classList.add("act")}}>
										
									</div>
								</div>
							</div>
							:""
						}
						{ (p.mediaNb===2) ?
							<div className="row w-100 h-100">
								<div className="col-8 p-2 pe-2 pb-1 " >
									<div className="rounded rounded-5 bg-dark w-100 h-100" onClick={(e) => {e.target.parentElement.parentElement.parentElement.children[2].classList.add("act")}}>
										
									</div>
								</div>
								<div className="col-4 p-2 d-flex flex-column gap-3">
										<div className="w-100 bg-dark rounded rounded-5 h-100" onClick={(e) => {e.target.parentElement.parentElement.parentElement.children[2].classList.add("act")}}>
											
										</div>
								</div>
							</div>
							:""
						}
						{ (p.mediaNb>=3) ?
							<div className="row w-100 h-100">
								<div className="col-6 p-2 pe-2 pb-1 " >
									<div className="rounded rounded-5 bg-dark w-100 h-100" onClick={(e) => {e.target.parentElement.parentElement.parentElement.children[2].classList.add("act")}}>
										
									</div>
								</div>
								<div className="col-3 p-2 d-flex flex-column gap-3">
										<div className="w-100 bg-dark rounded rounded-5 h-100" onClick={(e) => {e.target.parentElement.parentElement.parentElement.children[2].classList.add("act")}} >
											
										</div>
								</div>
								<div className="col-3 p-2 d-flex flex-column gap-3">
										<div className="w-100 bg-dark rounded rounded-5 h-100" onClick={(e) => {e.target.parentElement.parentElement.parentElement.children[2].classList.add("act")}} >
											
										</div>
								</div>
							</div>
							:""
						}
						<div className="d-flex justify-content-between align-items-center w-100 mb-2 p-1">
							<div className="d-flex align-items-center">
								<span className="d-flex align-items-center comment" onClick={(e) => {
									console.log(e.target.parentElement.parentElement.parentElement.children[1])
									e.target.parentElement.parentElement.parentElement.children[1].classList.contains("act") ?
									e.target.parentElement.parentElement.parentElement.children[1].classList.remove("act"):
									console.log("fermer")
									setTimeout(() => e.target.parentElement.parentElement.parentElement.children[0].classList.add("act"),500)
										}}>
									<i className="far fa-heart fa-2x me-2"></i>
									{p.commentaireNb}
								</span>
								<span className="ms-4 d-flex align-items-center" onClick={(e) => { 
									console.log(e.target.parentElement.parentElement.parentElement.children[0])
									e.target.parentElement.parentElement.parentElement.children[0].classList.contains("act") ?
									e.target.parentElement.parentElement.parentElement.children[0].classList.remove("act"):
									console.log("fermer")
									setTimeout(() => e.target.parentElement.parentElement.parentElement.children[1].classList.add("act"),500)
								}}>
									<i className="far fa-comment fa-2x me-2"></i>
									{p.likeNb}
								</span>
							</div>
							<i className="fas fa-share px-2 fa-2x"></i>
						</div>
					</div>
					
					
				</div>
				:
				<div className="d-flex flex-column align-items-center w-100 mb-3 bg-white rounded rounded-5 p-3">
					<div className="d-flex justify-content-between align-items-center w-100 mb-2">
						<div className="d-flex align-items-center">
							<span className="rounded rounded-5 me-2 bg-dark storyProfile"style={{backgroundImage:`url(${p.user.pp})`}}></span>
							<div className="d-flex flex-column ">	
								<strong className="text-secondary text-capitalize ps-2">{p.user.username}</strong>
								<span className="text-secondary ps-2">{p.date}</span>
							</div>
						</div>
						<i className="fas fa-angle-right px-2 fa-2x"></i>
					</div>
					<h4 className='text-start text-left w-100'>Défi: {p.defi.titre}</h4>
					<h5 className='text-start text-left w-100'>Finis le {p.defi.dateFin} </h5>
					<ul className='w-100 p-0 mt-4'>
						<span className='p-0 fw-bold'>Les Règles du Défis:</span>
						{p.defi.regle.map(r => 
							<li className='ms-3'><span>{r.titre}</span>: <span>{r.valeur}</span></li>
							)}
					</ul>
					<div className="d-flex align-items-center w-100">
						<button className="btn btn-dark me-3">
							Participer au Défi
						</button>
						<button className="btn btn-dark">
							Suivre le Défi
						</button>
						<p>Déja {p.participantNb} participants</p>
					</div>
				</div>
	
			}
		</div>
		)
    return (
        <div className="">		
				{posts}
			</div>
    );
}

export default Post;
